UPDATE `grid_basic_config` SET `id`='trainingItemApportion', `title`='项目分派', `can_search`='1', `can_add`='1', `can_delete`='1', `can_update`='1', `can_batch_add`='0', `can_projections`='0', `first_search`='0', `entity_class`='com.whaty.domain.bean.TrainingItem', `note`=NULL, `list_type`='1', `dc`='', `flag_active`='4028809c1d625bcf011d66fd0dda0006', `flag_bak`=NULL, `dateFormat`=NULL, `sqlstr`='SELECT\n  this.id as id,\n  peUnit.name as unitName,\n  peUnit.id as unitId,\n  this.code as code,\n  this.name as name,\n  ifnull(apt.name,\'未分派\') as isApportion,\n  enumConstByFlagItemStatus.name as itemStatus,\n  enumConstByFlagItemStatus.id as itemStatusId,\n  this.create_date as createDate,\n  enumConstByFlagTrainingType.name as trainingType,\n  enumConstByFlagTrainingTarget.name as trainingTarget,\n  enumConstByFlagTrainingType.id as trainingTypeId,\n  enumConstByFlagTrainingTarget.id as trainingTargetId,\n  this.training_start_time as trainingStartTime,\n  this.training_end_time as trainingEndTime,\n  this.training_person_number as trainingPersonNumber,\n  this.total_fee as totalFee,\n  this.put_on_record_time as putOnRecordTime,\n  this.apportion_date as apportionDate,\n  this.arrange_date as arrangeDate,\n  apportionUser.LOGIN_ID as apportionUser,\n  arrangeUnit.name as arrangeUnitName,\n  arrangeUser.LOGIN_ID as arrangeUser\nFROM\n	training_item this \nINNER JOIN pe_unit peUnit on peUnit.id=this.fk_unit_id AND [peUnit|peUnit.id]\nINNER JOIN enum_const enumConstByFlagItemStatus on enumConstByFlagItemStatus.id=this.flag_item_status\nAND enumConstByFlagItemStatus.code=\'3\' \nINNER JOIN enum_const enumConstByFlagTrainingType on enumConstByFlagTrainingType.id=this.flag_training_type\nINNER JOIN enum_const enumConstByFlagTrainingTarget on enumConstByFlagTrainingTarget.id=this.flag_training_target\nINNER JOIN enum_const enumConstByFlagTrainingItemType on enumConstByFlagTrainingItemType.id=this.flag_training_item_type\nLEFT JOIN enum_const apt on apt.id=this.flag_is_apportion\nLEFT JOIN sso_user apportionUser on apportionUser.id=this.fk_apportion_user_id\nLEFT JOIN sso_user arrangeUser on arrangeUser.id=this.fk_arrange_user_id\nLEFT JOIN pe_unit arrangeUnit on arrangeUnit.id=this.fk_arrange_unit_id\nLEFT JOIN pe_class cl on cl.fk_training_item_id=this.id\nWHERE cl.id is null \nAND this.fk_arrange_unit_id is null\nGROUP BY this.id', `statistics`=NULL, `s_note`=NULL, `update_note`=NULL, `is_enum`=NULL, `countsql`=NULL, `isGroupSql`='0', `LIST_FUNCTION`='0', `FLAG_INTE_ACTIVE`=NULL, `fk_web_site_id`='4028ae316a09ee85016a09f4d7640000', `can_checked_all`='1', `add_mode`=NULL, `add_cmp_name`=NULL, `fk_chart_id`=NULL WHERE (`id`='trainingItemApportion');
