INSERT INTO `pe_print_template` (`ID`, `TITLE`, `CODE`, `PATH`, `THUMB`, `TEAM`, `NUM`, `VARIABLE`, `MODIFY_DATE`, `DEFAULT_PATH`, `SEARCH_SQL`, `SEARCH_TYPE`, `print_page_size`, `site_code`) VALUES ('classPlaceUsage', '教室使用情况', 'word', NULL, NULL, NULL, NULL, NULL, NULL, '/templatefile/place/word/class_place_usage.docx', 'SELECT\n	c.id as classId,\n	c.name as cn,\n	un.name as un,\n	m.true_name as pu,\n	date_format(now(), \'%Y-%m-%d\') as pd\nFROM\n	pe_class c\nINNER JOIN pe_unit un ON un.id = c.fk_unit_id\nINNER JOIN pe_manager m ON ${m.fk_sso_user_id|currentUserId}\nGROUP BY\n	c.create_date;\nSELECT\n	cct.training_date as l_ud,\n	concat(ct.start_time, \' - \', ct.end_time) as l_ut,\n	pp.name as l_pn,\n	pu.name as l_pu,\n	pp.charges as l_pr\nFROM\n	class_course_timetable cct\nINNER JOIN class_course cc ON cc.id = cct.fk_class_course_id\nINNER JOIN course_time ct ON ct.id = cct.fk_course_time_id\nINNER JOIN pe_place pp ON pp.id = cct.fk_place_id\nINNER JOIN pe_unit pu ON pu.id = pp.fk_place_unit\nWHERE\n	${cc.fk_class_id|classId}\nGROUP BY\n	cct.training_date;', '2', NULL, 'training');
INSERT INTO `pe_print_template` (`ID`, `TITLE`, `CODE`, `PATH`, `THUMB`, `TEAM`, `NUM`, `VARIABLE`, `MODIFY_DATE`, `DEFAULT_PATH`, `SEARCH_SQL`, `SEARCH_TYPE`, `print_page_size`, `site_code`) VALUES ('trainingPutOnRecord', '项目备案表', 'word', NULL, NULL, NULL, NULL, NULL, NULL, '/templatefile/item/word/training_put_on_record.docx', 'SELECT\n	ti.id as itemId,\n	ti.name as tn,\r\n	ti.name as tn1,\r\n	ti.name as tn,\n	un.name as tu,\n	it.name as itt,\n	ti.code as tc,\n	tt.name as tt,\n	`to`.name as `to`,\n	ti.training_person_number as pn,\n	ifnull(ti.training_period, 0) as tp,\n	TRUNCATE(ifnull(ti.teacher_fee, 0), 0) as tf,\n	TRUNCATE(ifnull(ti.material_fee, 0), 0) as mf,\n	TRUNCATE(ifnull(ti.enroll_fee, 0), 0) as ef,\n	TRUNCATE(ifnull(ti.food_fee, 0), 0) as ff,\n	TRUNCATE(ifnull(ti.transport_fee, 0), 0) as trf,\n	TRUNCATE(ifnull(ti.teaching_fee, 0), 0) as tef,\n	TRUNCATE(ifnull(ti.site_fee, 0), 0) as sf,\n	TRUNCATE(ifnull(ti.other_fee, 0), 0) as of,\n	TRUNCATE(ifnull(ti.teacher_fee, 0) +\n	ifnull(ti.material_fee, 0) +\n	ifnull(ti.enroll_fee, 0) +\n	ifnull(ti.food_fee, 0) +\n	ifnull(ti.transport_fee, 0) +\n	ifnull(ti.teaching_fee, 0) +\n	ifnull(ti.site_fee, 0) +\n	ifnull(ti.other_fee, 0), 0) as rtf,\r\n	m.true_name as pu,\r\n	date_format(now(), \'%Y-%m-%d\') as pd\nFROM\n	training_item ti\nINNER JOIN enum_const it on it.id = ti.flag_training_item_type\ninner join pe_unit un on un.id = ti.fk_unit_id\ninner join enum_const tt on tt.id = ti.flag_training_type\ninner join enum_const `to` on `to`.id = ti.flag_training_target\ninner join enum_const st on st.id = ti.flag_item_status\r\ninner join sso_user ss on ${ss.id|currentUserId}\r\ninner join pe_manager m on m.fk_sso_user_id = ss.id\nWHERE\n	${ti.id|ids}\norder by ti.create_date;\nSELECT\n	m.true_name as l_cu,\n	date_format(cfr.check_time, \'%Y-%m-%d %H:%i:%s\') as l_cd\nFROM\n	check_flow cf\nINNER JOIN check_flow_record cfr ON cfr.fk_check_flow_id = cf.id\nINNER JOIN enum_const ac on ac.id = cf.flag_active\nINNER JOIN enum_const ty on ty.id = cf.flag_flow_type\nINNER JOIN pe_manager m on m.id = cfr.operate_user\nWHERE\n	ac.code = \'1\'\nAND ty.code in (\'1\', \'2\')\nAND ${cf.fk_item_id|itemId};', '2', NULL, 'training');
INSERT INTO `pe_print_template` (`ID`, `TITLE`, `CODE`, `PATH`, `THUMB`, `TEAM`, `NUM`, `VARIABLE`, `MODIFY_DATE`, `DEFAULT_PATH`, `SEARCH_SQL`, `SEARCH_TYPE`, `print_page_size`, `site_code`) VALUES ('trainingSettleAccount', '培训经费预算表', 'word', NULL, NULL, NULL, NULL, NULL, NULL, '/templatefile/account/word/training_settle_account.docx', 'SELECT\n	c.id AS classId,\n	ti. NAME AS tn,\n	un. NAME AS tu,\n	c. NAME AS cn,\n	c. CODE AS cc,\n	c.training_person_number as pn,\n	c.training_day_number as td,\n	TRUNCATE (ifnull(c.training_total_fee, 0), 0) as ttf,\n	TRUNCATE (ifnull(c.teacher_fee, 0), 0) AS tf,\n	TRUNCATE (ifnull(c.material_fee, 0), 0) AS mf,\n	TRUNCATE (ifnull(c.enroll_fee, 0), 0) AS ef,\n	TRUNCATE (ifnull(c.food_fee, 0), 0) AS ff,\n	TRUNCATE (ifnull(c.transport_fee, 0), 0) AS trf,\n	TRUNCATE (ifnull(c.teaching_fee, 0), 0) AS tef,\n	TRUNCATE (ifnull(c.site_fee, 0), 0) AS sf,\n	TRUNCATE (ifnull(c.other_fee, 0), 0) AS of,\n	TRUNCATE (\n		ifnull(c.teacher_fee, 0) + ifnull(c.material_fee, 0) + ifnull(c.enroll_fee, 0) + ifnull(c.food_fee, 0) + ifnull(c.transport_fee, 0) + ifnull(c.teaching_fee, 0) + ifnull(c.site_fee, 0) + ifnull(c.other_fee, 0),\n		0\n	) AS rtf,\n	au.true_name as au,\n	m.true_name AS pu,\n	date_format(now(), \'%Y-%m-%d\') AS pd\nFROM\n	pe_class c\nINNER JOIN training_item ti ON ti.id = c.fk_training_item_id\nINNER JOIN enum_const st ON st.id = c.flag_settle_account_status\nINNER JOIN pe_unit un ON un.id = c.fk_unit_id\nINNER JOIN check_flow cf ON cf.fk_item_id = c.id\nINNER JOIN pe_manager au on au.fk_sso_user_id = cf.create_by\nINNER JOIN enum_const ty ON ty.id = cf.flag_flow_type\nINNER JOIN enum_const ac ON ac.id = cf.flag_active\nINNER JOIN pe_manager m ON ${m.fk_sso_user_id|currentUserId}\nWHERE\n	${c.id|ids}\nAND ty. CODE = \'3\'\nAND ac. CODE = \'1\'\nAND st. CODE = \'2\'\nORDER BY\n	ti.create_date;\n\nSELECT\n	m.true_name as l_cu,\n	date_format(cfr.check_time, \'%Y-%m-%d %H:%i:%s\') as l_cd\nFROM\n	check_flow cf\nINNER JOIN check_flow_record cfr ON cfr.fk_check_flow_id = cf.id\nINNER JOIN enum_const ac on ac.id = cf.flag_active\nINNER JOIN enum_const ty on ty.id = cf.flag_flow_type\nINNER JOIN pe_manager m on m.id = cfr.operate_user\nWHERE\n	ac.code = \'1\'\nAND ty.code = \'3\'\nAND ${cf.fk_item_id|classId};', '2', NULL, 'training');
