UPDATE `grid_basic_config` SET `id`='analyseFeeByArea', `title`='按地区汇总查询', `can_search`='1', `can_add`=NULL, `can_delete`=NULL, `can_update`=NULL, `can_batch_add`=NULL, `can_projections`='0', `first_search`='0', `entity_class`=NULL, `note`=NULL, `list_type`='1', `dc`=NULL, `flag_active`='4028809c1d625bcf011d66fd0dda0006', `flag_bak`=NULL, `dateFormat`=NULL, `sqlstr`='SELECT\n	r.id AS id,\n	r.trainingAreaId as trainingAreaId,\n	r.trainingArea as trainingArea,\n	sum(r.totalEntrustedUnit) AS totalEntrustedUnit,\n	sum(r.totalClass) AS totalClass,\n	sum(r.totalStudent) AS totalStudent,\n	r.startTime as startTime,\n	r.endTime as endTime,\n	sum(r.totalFee) AS totalFee,\n	r.feeStatus as feeStatus\nFROM\n	(\n		SELECT\n			c.id AS id,\n			ta.id as trainingAreaId,\n			ta.name as trainingArea,\n      c.start_time as startTime,\n			c.end_time as endTime,\n			count(distinct eu.id) as totalEntrustedUnit,\n			count(distinct c.id) AS totalClass,\n			count(distinct stu.id) AS totalStudent,\n			c.training_total_fee AS totalFee,\n			st.name as feeStatus\n		FROM\n			training_item ti\n		INNER JOIN pe_Class c ON c.fk_training_item_id = ti.id\n		INNER JOIN pe_unit un ON un.id = c.fk_unit_id AND [peUnit|un.id]\n		INNER JOIN enum_const it ON it.id = ti.flag_training_item_type\n		INNER JOIN entrusted_unit eu on eu.id = ti.fk_entrusted_unit_id\n		LEFT JOIN pe_area ta on ta.id = eu.fk_area_id\n		LEFT JOIN pe_student stu ON stu.fk_class_id = c.id\n		INNER JOIN enum_const st on st.id = c.flag_settle_account_status\n    where c.site_code = \'${siteCode}\'\n		AND it.code = \'2\'\n		GROUP BY\n			c.id\n	) r\nGROUP BY\n	trainingAreaId', `statistics`=NULL, `s_note`=NULL, `update_note`=NULL, `is_enum`=NULL, `countsql`=NULL, `isGroupSql`=NULL, `LIST_FUNCTION`='0', `FLAG_INTE_ACTIVE`=NULL, `fk_web_site_id`='4028ae316a09ee85016a09f4d7640000', `can_checked_all`='0', `add_mode`='dialog', `add_cmp_name`=NULL, `fk_chart_id`=NULL WHERE (`id`='analyseFeeByArea');
