UPDATE `grid_basic_config` SET `id`='classHotelDetail', `title`='住宿安排', `can_search`='1', `can_add`=NULL, `can_delete`=NULL, `can_update`=NULL, `can_batch_add`=NULL, `can_projections`='0', `first_search`='0', `entity_class`='com.whaty.domain.bean.PeClass', `note`=NULL, `list_type`='1', `dc`='', `flag_active`='4028809c1d625bcf011d66fd0dda0006', `flag_bak`=NULL, `dateFormat`=NULL, `sqlstr`='SELECT\n	r.id AS id,\n	r.unitName AS unitName,\n	r.classCode AS classCode,\n	r.className AS className,\n	r.linkman AS linkman,\n	r.linkPhone AS linkPhone,\n	r.studentNumber,\n	r.startTime AS startTime,\n	r.endTime AS endTime,\n	r.note AS note,\n	sum(r.totalPrice) AS totalPrice,\n	r.applyTime AS applyTime,\n	r.applyUser AS applyUser,\n	r.detailId AS detailId\nFROM\n	(\n		SELECT\n			c.id AS id,\n			un. NAME AS unitName,\n			c. CODE AS classCode,\n			c. NAME AS className,\n			tea.true_name AS linkman,\n			tea.mobile AS linkPhone,\n			count(DISTINCT stu.id) AS studentNumber,\n			chd.start_time AS startTime,\n			chd.end_time AS endTime,\n			chd.note AS note,\n			cha.female_price * cha.female_room_number + cha.male_price * cha.male_room_number AS totalPrice,\n			chd.apply_time AS applyTime,\n			au.login_id AS applyUser,\n			chd.id AS detailId\n		FROM\n			pe_class c\n		LEFT JOIN pe_student stu ON stu.fk_class_id = c.id\n		INNER JOIN class_hotel_detail chd ON chd.id = c.fk_class_hotel_detail_id\n		INNER JOIN pe_unit un ON un.id = c.fk_unit_id\n		AND [peUnit|un.id]\n		LEFT JOIN class_hotel_arrange cha ON cha.fk_class_hotel_detail_id = chd.id\n		INNER JOIN sso_user au ON au.id = chd.apply_user\n		LEFT JOIN pe_teacher tea ON tea.id = c.fk_class_master_id\n		WHERE\n			c.site_code = \'${siteCode}\'\n		GROUP BY\n			cha.id\n	) r\nGROUP BY\n	r.id', `statistics`=NULL, `s_note`=NULL, `update_note`=NULL, `is_enum`=NULL, `countsql`=NULL, `isGroupSql`=NULL, `LIST_FUNCTION`='0', `FLAG_INTE_ACTIVE`=NULL, `fk_web_site_id`='4028ae316a09ee85016a09f4d7640000', `can_checked_all`='1', `add_mode`='dialog', `add