UPDATE `grid_basic_config` SET `id`='cooperationTrainingItemApply', `title`='委托项目申报', `can_search`='1', `can_add`='1', `can_delete`='1', `can_update`='1', `can_batch_add`='0', `can_projections`='0', `first_search`='0', `entity_class`='com.whaty.domain.bean.TrainingItem', `note`=NULL, `list_type`='1', `dc`='', `flag_active`='4028809c1d625bcf011d66fd0dda0006', `flag_bak`=NULL, `dateFormat`=NULL, `sqlstr`='SELECT\n  this.id as id,\n  peUnit.name as unitName,\n  peUnit.id as unitId,\n  this.code as code,\n  this.name as name,\n  enumConstByFlagItemStatus.name as itemStatus,\n  enumConstByFlagItemStatus.id as itemStatusId,\n  createBy.LOGIN_ID as createByLoginId,\n  trainingItemExtend.id as trainingItemExtendId,\n  trainingItemExtend2.id as trainingItemExtendId2,\n  trainingItemExtend3.id as trainingItemExtendId3,\n  trainingItemExtend4.id as trainingItemExtendId4,\n  this.create_date as createDate,\n  checkUser.LOGIN_ID as checkUserLoginId,\n  this.check_date as checkDate,\n  enumConstByFlagTrainingType.name as trainingType,\n  enumConstByFlagTrainingTarget.name as trainingTarget,\n  enumConstByFlagTrainingType.id as trainingTypeId,\n  enumConstByFlagTrainingTarget.id as trainingTargetId,\n  this.training_start_time as trainingStartTime,\n  this.training_end_time as trainingEndTime,\n  this.training_person_number as trainingPersonNumber,\n  this.teacher_fee as teacherFee,\n  this.room_fee as roomFee,\n  this.food_fee as foodFee,\n  this.transport_fee as transportFee,\n  this.teaching_fee as teachingFee,\n  this.site_fee as siteFee,\n  this.other_fee as otherFee,\n  this.total_fee as totalFee,\n  this.put_on_record_time as putOnRecordTime,\n  this.collect_fee_unit as collectFeeUnit,\n  cooperateUnit.name as cooperateUnitName,\n  cooperateUnit.id as cooperateUnitId,\n  enumConstByFlagCooperateUnitType.name as cooperateUnitType,\n  cooperateUnitArea.name as cooperateUnitAreaName,\n  cooperateUnit.linkman as cooperateUnitLinkman,\n  cooperateUnit.telephone as cooperateUnitTelephone,\n  cooperateUnit.linkman_position as cooperateUnitLinkmanPosition,\n  cooperateUnit.division_proportion as cooperateUnitDivisionProportion,\n  entrustedUnit.name as entrustedUnitName,\n  enumConstByFlagEntrustedUnitType.name as entrustedUnitTypeName,\n  entrustedUnitArea.name as entrustedUnitAreaName,\n  enumConstByFlagInfoSource.name as infoSourceName,\n  enumConstByFlagInfoSource.id as infoSourceId,\n  entrustedUnit.id as entrustedUnitId,\n  this.head_name as headName\nFROM\n	training_item this \nINNER JOIN pe_unit peUnit on peUnit.id=this.fk_unit_id AND [peUnit|peUnit.id]\nINNER JOIN enum_const enumConstByFlagItemStatus on enumConstByFlagItemStatus.id=this.flag_item_status\nINNER JOIN enum_const enumConstByFlagTrainingType on enumConstByFlagTrainingType.id=this.flag_training_type\nINNER JOIN enum_const enumConstByFlagTrainingTarget on enumConstByFlagTrainingTarget.id=this.flag_training_target\nINNER JOIN enum_const enumConstByFlagTrainingItemType on enumConstByFlagTrainingItemType.id=this.flag_training_item_type\nAND enumConstByFlagTrainingItemType.code = \'2\'\nINNER JOIN enum_const et1 on et1.NAMESPACE=\'FlagItemExtendType\' and et1.code=\'1\'\nINNER JOIN enum_const et2 on et2.NAMESPACE=\'FlagItemExtendType\' and et2.code=\'2\'\nINNER JOIN enum_const et3 on et3.NAMESPACE=\'FlagItemExtendType\' and et3.code=\'3\'\nINNER JOIN enum_const et4 on et4.NAMESPACE=\'FlagItemExtendType\' and et4.code=\'4\'\nLEFT JOIN sso_user createBy on createBy.id=this.create_by\nLEFT JOIN sso_user checkUser on checkUser.id=this.fk_check_user_id\nLEFT JOIN training_item_extend trainingItemExtend on trainingItemExtend.fk_item_id=this.id\nAND trainingItemExtend.flag_item_extend_type=et1.id\nLEFT JOIN training_item_extend trainingItemExtend2 on trainingItemExtend2.fk_item_id=this.id\nAND trainingItemExtend2.flag_item_extend_type=et2.id\nLEFT JOIN training_item_extend trainingItemExtend3 on trainingItemExtend3.fk_item_id=this.id\nAND trainingItemExtend3.flag_item_extend_type=et3.