INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('placeUsageDetail', '培训地点使用详情', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	cct.id as id,\n	un.name as unitName,\n	c.name as className,\n	c.code as classCode,\n	count(DISTINCT stu.id) as studentNumber,\n	c.start_time as startTime,\n	c.end_time as endTime,\n	ty.name as courseType,\n	p.name as placeName,\n	pu.name as placeUnit,\n	cou.name courseName,\n	tea.true_name as teacherName,\n	cct.training_date as limitDate,\n	ct.start_time as useStartTime,\n	ct.end_time as useEndTime,\n        ct.name as courseTime\nFROM\n	class_course_timetable cct\nINNER JOIN class_course cc ON cc.id = cct.fk_class_course_id\nINNER JOIN pe_class c ON c.id = cc.fk_class_id\nINNER JOIN pe_place p ON p.id = cct.fk_place_id\nINNER JOIN course_time ct ON ct.id = cct.fk_course_time_id\nINNER JOIN pe_unit un ON un.id = c.fk_unit_id AND [peUnit|un.id]\nINNER JOIN pe_unit pu ON pu.id = p.fk_place_unit\nINNER JOIN pe_course cou ON cou.id = cc.fk_course_id\nLEFT JOIN pe_teacher tea ON tea.id = cou.fk_teacher_id\nLEFT JOIN pe_student stu on stu.fk_class_id = c.id\nINNER JOIN enum_const ty on ty.id = cou.flag_course_type\nWHERE\n       [unitId|un.id]\nAND [placeUnit|pu.id]\nAND c.site_code = \'${siteCode}\'\nGROUP BY \n	cct.id', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
