UPDATE `grid_basic_config` SET `sqlstr`='SELECT\n	c.id as id,\n	un.name as unitName,\n	c.code as classCode,\n	c.name as className,\n	tea.true_name as linkman,\n	tea.mobile as linkPhone,\n	count(stu.id) as studentNumber,\n	c.start_time as startTime,\n	c.end_time as endTime,\n	c.site_fee as placeFee,\n	pt.id as placeTypeId,\n	pt.name as placeType,\n	c.apply_time as applyTime,\n	au.login_id as applyUser,\n  c.place_note as placeNote,\n  GROUP_CONCAT(DISTINCT pp.name ORDER BY pp.name) as placeGroup\nFROM\n	pe_class c\nLEFT JOIN pe_student stu on stu.fk_class_id = c.id\nINNER JOIN pe_unit un on un.id = c.fk_unit_id AND [peUnit|un.id]\nINNER JOIN enum_const pt on pt.id = c.flag_place_type\nINNER JOIN sso_user au on au.id = c.apply_user\nLEFT JOIN pe_teacher tea on tea.id = c.fk_class_master_id\nLEFT JOIN class_course cc ON cc.fk_class_id = c.id\nLEFT JOIN class_course_timetable cct ON cc.id = cct.fk_class_course_id\nLEFT JOIN pe_place pp on pp.id = cct.fk_place_id\nwhere c.site_code = \'${siteCode}\'\nGROUP BY\n	c.id' WHERE (`id`='classTrainingPlaceManage');
