UPDATE `grid_basic_config` SET `sqlstr`='SELECT\n  item.id as id,\n  unit.name as unitName,\n  item.name as itemName,\n  item.enroll_end_time as endTime,\n  item.code as itemCode,\n  item.total_fee as enrollFee,\n  count(stu.id) as enrollCount,\n  count(ft.id) as payCount,\n  round(count(ft.id)*stu.enroll_fee,2) as payFee,\n  count(st.id) as returnCount,\n  round(count(st.id)*stu.enroll_fee,2) as returnFee\nFROM\n	training_item item\nINNER JOIN pe_unit unit on unit.id=item.fk_unit_id AND [peUnit|unit.id]\nINNER JOIN pe_student stu ON stu.fk_training_item_id=item.id\nleft JOIN enum_const ft ON ft.id = stu.flag_fee_status\nand ft.code=\'1\'\nleft JOIN enum_const st ON st.id = stu.flag_student_status\nand st.code=\'2\'\nwhere item.site_code = \'${siteCode}\'\nGROUP BY item.id' WHERE (`id`='studentEnrollStatistics');

