UPDATE `pe_print_template` SET `SEARCH_SQL`='SELECT\n	c.id as classId,\n	c.name as cn,\n	un.name as un,\n	m.true_name as pu,\n	date_format(now(), \'%Y-%m-%d\') as pd\nFROM\n	pe_class c\nINNER JOIN pe_unit un ON un.id = c.fk_unit_id\nINNER JOIN pe_manager m ON ${m.fk_sso_user_id|currentUserId}\r\nwhere\r\n	${c.id|ids}\nGROUP BY\n	c.create_date;\nSELECT\n	cct.training_date as l_ud,\n	concat(ct.start_time, \' - \', ct.end_time) as l_ut,\n	pp.name as l_pn,\n	pu.name as l_pu,\n	pp.charges as l_pr\nFROM\n	class_course_timetable cct\nINNER JOIN class_course cc ON cc.id = cct.fk_class_course_id\nINNER JOIN course_time ct ON ct.id = cct.fk_course_time_id\nINNER JOIN pe_place pp ON pp.id = cct.fk_place_id\nINNER JOIN pe_unit pu ON pu.id = pp.fk_place_unit\nWHERE\n	${cc.fk_class_id|classId};' WHERE (`template_code`='classPlaceUsage');
