INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('teacherWorkloadAnalyse', '教师工作量统计', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	tea.id AS teacherId,\n	tea.true_name AS teacherName, \n        unit. id  AS unitId,\n	unit. NAME AS unitName,\n	COUNT(DISTINCT IF(c.isEndedClass,c.classId,NULL)) AS endedClassNum, \n	COUNT(DISTINCT IF(c.isEndedClass,c.courseTableId,NULL)) AS courseNumOfEndedClass,\n	SUM(IFNULL(IF(c.isEndedClass,c.courseTeacherFee,NULL),0)) AS teacherFeeSumOfEndedClass, \n	c.classEndTime AS endTimeOfEndedClass, \n        now()  AS  timePeriod,\n	c.classStartTime AS startTimeOfEndedClass\nFROM\n	pe_teacher tea\nINNER JOIN pe_unit unit ON unit.id = tea.fk_unit_id -- AND [peUnit|unit.id] \nINNER JOIN (\n	SELECT \n		cl.id AS classId,\n		cl.start_time AS classStartTime,\n		cl.end_time AS classEndTime,\n		c.fk_teacher_id AS courseTeacherId,\n		cct.id AS courseTableId,\n		cct.teacher_fee AS courseTeacherFee,\n	  IF(DATEDIFF(cl.end_time, NOW()) < 0,TRUE,NULL) AS isEndedClass,\n		IF(DATEDIFF(cl.start_time, NOW()) >= 0,TRUE,NULL) AS isExpectedOpenClass, \n		IF(DATEDIFF(cl.start_time, NOW()) < 0 & DATEDIFF(cl.end_time, NOW()) > 0,TRUE,NULL) AS isTrainingClass\n	FROM pe_course c\n	INNER JOIN class_course cc ON cc.fk_course_id = c.id\n	INNER JOIN pe_class cl ON cl.id = cc.fk_class_id\n	LEFT JOIN class_course_timetable cct ON cct.fk_class_course_id = cc.id\n	WHERE\n                 [ConditionSign]\n		 AND  c.site_code = \'${siteCode}\' \n) c ON tea.id = c.courseTeacherId\nWHERE\n	tea.site_code = \'${siteCode}\' \nGROUP BY tea.id\nHAVING  endedClassNum>0', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('teacherWorkloadAnalyseDetail', '教师工作量详情', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n        tea.id AS teacherId,\n	tea.true_name AS teacherName,\n	clunit. NAME AS classUnitName, \n	cl.name AS className, \n	COUNT(DISTINCT stu.id) AS stuCount, \n	cl.start_time AS classStartTime, \n	IFNULL(CONCAT(pu.name,\'-\',p.name),\'\') AS teachingPlace, \n	c.name AS courseName, \n        now()  AS  timePeriod,\n	IFNULL(cct.teacher_fee,0) AS teacherFee \nFROM\n	pe_teacher tea\nINNER JOIN pe_unit unit ON unit.id = tea.fk_unit_id AND [unitId|unit.id]  \nINNER JOIN pe_course c ON c.fk_teacher_id =tea.id AND c.site_code =  \'${siteCode}\' \nINNER JOIN class_course cc ON cc.fk_course_id = c.id \nLEFT JOIN class_course_timetable cct ON cct.fk_class_course_id = cc.id \nINNER JOIN pe_class cl ON cl.id = cc.fk_class_id  \nINNER JOIN pe_unit clunit ON clunit.id = cl.fk_unit_id \nLEFT JOIN pe_student stu ON stu.fk_class_id = cl.id \nLEFT JOIN pe_place p ON p.id = cct.fk_place_id\nLEFT JOIN pe_unit pu ON p.fk_place_unit = pu.id \nWHERE\n        [ConditionSign]\n        AND  [teacherId|tea.id]\n	AND tea.site_code =  \'${siteCode}\'\n	AND DATEDIFF(cl.end_time, NOW()) < 0\nGROUP BY cl.id,c.id', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('unitDepartmentPersonnelWorkloadAnalyse', '单位部门人员工作量统计', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT \n	c.unitId AS unitId,\n	c.unitName AS unitName,\n	c.trueName AS trueName,\n	c.departmentId AS departmentId,\n	c.departmentName AS departmentName,\n	c.requireTotal AS requireTotal,\n	c.fallowUpNum AS fallowUpNum,\n	c.successfallowUpNum AS successfallowUpNum,\n	c.failurefallowUpNum AS failurefallowUpNum,\n        now()  AS  timePeriod,\n	IFNULL(CONCAT(ROUND(c.successfallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as successfallowUpRate, -- 跟进成功率  跟进成功数/总数\n	IFNULL(CONCAT(ROUND(c.failurefallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as failurefallowUpRate, -- 失败率\n	COUNT(c.isExpectedOpenClass) AS expectedOpenClassNum, -- 预计开设班级数量 \n\n  -- 以下部分都是带班完成的统计\n	COUNT(c.isEndedClass) AS endedClassNum, -- 带班完成数\n	SUM(IF(c.isEndedClass,c.studentNumOfClass,0)) AS studentTotalNumOfEndedClass, -- 带班完成学员数\n  SUM(IF(c.isEndedClass,c.daysPeriodOfClass,0)) AS daysOfEndedClass, -- 带班完成天数\n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareBeforeTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareBeforeTrainingRateOfEndedClass, -- 训前是否准备率  准备数/班级数\n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareTrainingRateOfEndedClass, -- 训中是否准备率  准备数/班级数\n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareAfterTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareAfterTrainingRateOfEndedClass, -- 训后是否准备率  准备数/班级数\n	SUM(IF(c.isEndedClass,evaluateTotalNum,0)) AS evaluateTotalNumOfEndedClass, -- 评价总数\n	SUM(IF(c.isEndedClass,aCount,0)) AS excellentEvaluateTotalNumOfEndedClass, -- 优评总数\n	SUM(IF(c.isEndedClass,dCount,0)) AS badEvaluateTotalNumOfEndedClass, -- 差评总数\n	IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,evaluateTotalNum,0))/SUM(IF(c.isEndedClass,c.studentNumOfClass,0))*100,2),\'%\'),\'0.00%\') as evaluateRateOfEndedClass,\n	IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,aCount,0))/SUM(IF(c.isEndedClass,evaluateTotalNum,0))*100,2),\'%\'),\'0.00%\') as excellentEvaluateRateOfEndedClass, -- 优评率（优评数/评价数）\n	IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,dCount,0))/SUM(IF(c.isEndedClass,evaluateTotalNum,0))*100,2),\'%\'),\'0.00%\') as badEvaluateRateOfEndedClass -- 差评率（差评数/评价数）\nFROM (\n	-- 以班级进行分组\n	SELECT \n		unit.id AS unitId,\n		unit. NAME AS unitName, -- 单位\n		pm.id AS managerId,\n		pm.true_name AS trueName, -- 姓名\n		IFNULL(dt.id,\'nullDepartment\') AS departmentId,\n		IFNULL(dt.name,\'无部门\') as departmentName, \n                ri.create_time AS riCreateTime,\n		COUNT(DISTINCT ri.id) AS requireTotal, -- 市场数\n		COUNT(DISTINCT IF(ec.code = \'1\',ri.id,NULL)) AS waittingFallowUpNum, -- 待跟进\n		COUNT(DISTINCT IF(ec.code = \'2\',ri.id,NULL)) AS fallowUpNum, -- 跟进数\n		COUNT(DISTINCT IF(ec.code = \'3\',ri.id,NULL)) AS successfallowUpNum, -- 跟进成功数\n		COUNT(DISTINCT IF(ec.code = \'4\',ri.id,NULL)) AS failurefallowUpNum, -- 跟进失败数\n		COUNT(DISTINCT IF(ec.code = \'5\',ri.id,NULL)) AS extensionfallowUpNum, -- 跟进延期数\n		\n		-- 班级相关的\n		cl.id AS classId,\n		cl.start_time AS classStartTime,\n		cl.end_time AS classEndTime,\n		IF(DATEDIFF(cl.end_time, NOW()) < 0,TRUE,NULL) AS isEndedClass, -- 班级是否完成\n		IF(DATEDIFF(cl.start_time, NOW()) >= 0,TRUE,NULL) AS isExpectedOpenClass, -- 班级是否为未来开设班级\n		IF(DATEDIFF(cl.start_time, NOW()) < 0 & DATEDIFF(cl.end_time, NOW()) > 0,TRUE,NULL) AS isTrainingClass, -- 班级是否为正在进行中的\n		DATEDIFF(cl.end_time,cl.start_time) AS daysPeriodOfClass, -- 班级周期 （天为单位）\n		COUNT(DISTINCT stu.id) AS studentNumOfClass, -- 班级学员数\n		IF(pi.prepareCode = \'1\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareBeforeTraining, -- 训前是否准备\n		IF(pi.prepareCode = \'2\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareTraining, -- 训中是否准备\n		IF(pi.prepareCode = \'3\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareAfterTraining, -- 训前是否准备\n\n		-- 评价数只统计班级已经结束的\n		COUNT(DISTINCT IF(oe.answerCode IS NOT NULL,oe.oeaId,NULL)) as evaluateTotalNum, -- 评价总数\n		COUNT(DISTINCT IF(oe.answerCode=\'0\',oe.oeaId,NULL)) AS dCount, -- 差评数\n		COUNT(DISTINCT IF(oe.answerCode=\'1\',oe.oeaId,NULL)) AS cCount, -- 中评数\n		COUNT(DISTINCT IF(oe.answerCode=\'2\',oe.oeaId,NULL)) AS bCount, -- 良评数\n		COUNT(DISTINCT IF(oe.answerCode=\'3\',oe.oeaId,NULL)) AS aCount -- 优评数\n	FROM \n		pe_manager pm\n	INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id  \n	LEFT JOIN enum_const dt ON dt.id = pm.flag_department_type\n	INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND fk_class_master_id = pm.id  -- 班级\n	INNER JOIN requirement_info ri on ri.fk_create_user_id=pm.id \n	INNER JOIN enum_const ec ON ec.id = ri.flag_follow_up_status  -- 需求\n	LEFT JOIN pe_student stu ON stu.fk_class_id = cl.id -- 学生\n\n	-- 准备统计\n	LEFT JOIN (\n		SELECT tpi.fk_manager_id AS managerId, tpi.fk_class_id AS classId,pt.code AS prepareCode,tpi.is_prepare AS isPrepare\n		FROM prepare_item pi\n		INNER JOIN enum_const pt ON pt.id = pi.flag_prepare_type -- 教师准备物品的阶段\n		LEFT JOIN teacher_prepare_item tpi ON tpi.fk_prepare_item_id = pi.id\n		WHERE pi.site_code = \'${siteCode}\'\n	) pi ON pi.managerId = pm.id AND pi.classId = cl.id\n\n	-- 评价\n	LEFT JOIN (\n		SELECT oea.fk_sso_user_id AS ssoUserId,ao.code AS answerCode,oea.id AS oeaId\n		FROM overall_evaluate oe\n		INNER JOIN enum_const va ON va. CODE = \'1\' AND oe.flag_isvalid = va.id   -- 有效\n		INNER JOIN overall_evaluate_option oeo ON oeo.fk_evaluate_id = oe.id\n		INNER JOIN enum_const ot ON ot.code=\'2\' AND oeo.flag_option_type = ot.id -- 班主任评价中的单选题\n		INNER JOIN enum_const qt ON qt.code=\'1\' AND oeo.flag_question_type = qt.id   -- 单选题\n		LEFT JOIN overall_evaluate_answer oea ON oea.fk_option_id = oeo.id\n		INNER JOIN enum_const ao ON ao.id = oea.answer -- 答案选项\n		WHERE	oe.site_code = \'${siteCode}\' -- 问卷\n	) oe ON oe.ssoUserId = stu.fk_sso_user_id\n	WHERE\n                [ConditionSign]\n                AND  [unitId|unit.id] \n		AND  [departmentId|IFNULL(dt.id,\'nullDepartment\')]\n		AND  pm.site_code = \'${siteCode}\'\n	GROUP BY cl.id\n) c\nGROUP BY c.managerId', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('unitDepartmentWorkloadAnalyse', '单位部门工作量统计', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT \n        c.unitId  AS unitId,\n 	c.departmentId AS departmentId,\n	c.unitName AS unitName,\n	c.departmentName AS departmentName,\n	c.requireTotal AS requireTotal,\n	c.fallowUpNum AS fallowUpNum,\n	c.successfallowUpNum AS successfallowUpNum,\n	c.failurefallowUpNum AS failurefallowUpNum,\n        now()  AS  timePeriod,\n	IFNULL(CONCAT(ROUND(c.successfallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as successfallowUpRate, \n	IFNULL(CONCAT(ROUND(c.failurefallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as failurefallowUpRate, \n	\n	c.expectedOpenClassNum AS expectedOpenClassNum,\n	c.endedClassNum AS endedClassNum,\n	c.studentTotalNumOfEndedClass AS studentTotalNumOfEndedClass,\n	c.evaluateTotalNum AS evaluateTotalNumOfEndedClass,\n	c.aCount AS excellentEvaluateTotalNumOfEndedClass,\n	c.dCount AS badEvaluateTotalNumOfEndedClass,\n	IFNULL(CONCAT(ROUND(c.evaluateTotalNum/c.studentTotalNumOfEndedClass*100,2),\'%\'),\'0.00%\') as evaluateRateOfEndedClass, -- 评价率（评价数/学员数）\n	IFNULL(CONCAT(ROUND(c.aCount/c.evaluateTotalNum*100,2),\'%\'),\'0.00%\') as excellentEvaluateRateOfEndedClass, -- 优评率（优评数/评价数）\n	IFNULL(CONCAT(ROUND(c.dCount/c.evaluateTotalNum*100,2),\'%\'),\'0.00%\') as badEvaluateRateOfEndedClass -- 差评率（差评数/评价数）\nFROM (\n	SELECT \n		unit.id AS unitId,\n		unit. NAME AS unitName, \n                ri.create_time AS riCreateTime,\n		cl.end_time AS classEndTime,\n		IFNULL(dt.id,\'nullDepartment\') AS departmentId,\n		IFNULL(dt.name,\'无部门\') as departmentName, \n		COUNT(DISTINCT ri.id) AS requireTotal, \n		COUNT(DISTINCT IF(ec.code = \'1\',ri.id,NULL)) AS waittingFallowUpNum, -- 待跟进\n		COUNT(DISTINCT IF(ec.code = \'2\',ri.id,NULL)) AS fallowUpNum, -- 跟进数\n		COUNT(DISTINCT IF(ec.code = \'3\',ri.id,NULL)) AS successfallowUpNum, -- 跟进成功数\n		COUNT(DISTINCT IF(ec.code = \'4\',ri.id,NULL)) AS failurefallowUpNum, -- 跟进失败数\n		COUNT(DISTINCT IF(ec.code = \'5\',ri.id,NULL)) AS extensionfallowUpNum, -- 跟进延期数\n		\n		COUNT(DISTINCT IF(DATEDIFF(cl.start_time, NOW()) >= 0,cl.id,NULL)) AS expectedOpenClassNum, -- 预计开设班级数量 带班数量  \n		COUNT(DISTINCT IF(DATEDIFF(cl.start_time, NOW()) < 0 & DATEDIFF(cl.end_time, NOW()) > 0,cl.id,NULL)) AS trainingClassNum, -- 正在进行中班级数量\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0,cl.id,NULL)) AS endedClassNum, -- 带班完成数\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0,stu.id,NULL)) AS studentTotalNumOfEndedClass, -- 带班完成学员数\n		\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode IS NOT NULL,oe.oeaId,NULL)) as evaluateTotalNum, -- 评价总数\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'0\',oe.oeaId,NULL)) AS dCount, -- 差评数\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'1\',oe.oeaId,NULL)) AS cCount, -- 中评数\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'2\',oe.oeaId,NULL)) AS bCount, -- 良评数\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'3\',oe.oeaId,NULL)) AS aCount -- 优评数\n	FROM \n		pe_manager pm\n	INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id  \n	LEFT JOIN enum_const dt ON dt.id = pm.flag_department_type\n	INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND cl.fk_class_master_id = pm.id \n	INNER JOIN requirement_info ri on ri.fk_create_user_id=pm.id AND ri.site_code =\'${siteCode}\'\n	INNER JOIN enum_const ec ON ec.id = ri.flag_follow_up_status\n	LEFT JOIN pe_student stu ON stu.fk_class_id = cl.id \n\n	LEFT JOIN (\n		SELECT oea.fk_sso_user_id AS ssoUserId,ao.code AS answerCode,oea.id AS oeaId\n		FROM overall_evaluate oe\n		INNER JOIN enum_const va ON va. CODE = \'1\' AND oe.flag_isvalid = va.id   \n		INNER JOIN overall_evaluate_option oeo ON oeo.fk_evaluate_id = oe.id\n		INNER JOIN enum_const ot ON ot.code=\'2\' AND oeo.flag_option_type = ot.id\n		INNER JOIN enum_const qt ON qt.code=\'1\' AND oeo.flag_question_type = qt.id  \n		LEFT JOIN overall_evaluate_answer oea ON oea.fk_option_id = oeo.id\n		INNER JOIN enum_const ao ON ao.id = oea.answer \n		WHERE	oe.site_code = \'${siteCode}\'\n	) oe ON oe.ssoUserId = stu.fk_sso_user_id\n	WHERE\n                [ConditionSign]\n		AND pm.site_code = \'${siteCode}\'\n	GROUP BY dt.id \n)  c', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('unitPersonnelWorkloadAnalyse', '单位人员工作量统计', '1', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT \n	c.unitId AS unitId,\n	c.unitName AS unitName,\n	c.trueName AS trueName,\n	c.departmentName AS departmentName,\n	c.requireTotal AS requireTotal,\n	c.fallowUpNum AS fallowUpNum,\n        now()  AS  timePeriod,\n        c.classEndTime AS classEndTime,\n	c.successfallowUpNum AS successfallowUpNum,\n	c.failurefallowUpNum AS failurefallowUpNum,\n	IFNULL(CONCAT(ROUND(c.successfallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as successfallowUpRate, \n	IFNULL(CONCAT(ROUND(c.failurefallowUpNum/c.requireTotal*100,2),\'%\'),\'0.00%\') as failurefallowUpRate, \n	COUNT(c.isExpectedOpenClass) AS expectedOpenClassNum, \n\n	COUNT(c.isEndedClass) AS endedClassNum,\n	SUM(IF(c.isEndedClass,c.studentNumOfClass,0)) AS studentTotalNumOfEndedClass, \n  SUM(IF(c.isEndedClass,c.daysPeriodOfClass,0)) AS daysOfEndedClass, \n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareBeforeTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareBeforeTrainingRateOfEndedClass, \n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareTrainingRateOfEndedClass, \n	IFNULL(CONCAT(ROUND(COUNT(IF(c.isEndedClass & isPrepareAfterTraining,TRUE,NULL))/COUNT(IF(c.isEndedClass,TRUE,NULL))*100,2),\'%\'),\'0.00%\') as isPrepareAfterTrainingRateOfEndedClass, \n	SUM(IF(c.isEndedClass,evaluateTotalNum,0)) AS evaluateTotalNumOfEndedClass, \n	SUM(IF(c.isEndedClass,aCount,0)) AS excellentEvaluateTotalNumOfEndedClass, \n	SUM(IF(c.isEndedClass,dCount,0)) AS badEvaluateTotalNumOfEndedClass, \n        IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,evaluateTotalNum,0))/SUM(IF(c.isEndedClass,c.studentNumOfClass,0))*100,2),\'%\'),\'0.00%\') as evaluateRateOfEndedClass,\n	IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,aCount,0))/SUM(IF(c.isEndedClass,evaluateTotalNum,0))*100,2),\'%\'),\'0.00%\') as excellentEvaluateRateOfEndedClass,\n	IFNULL(CONCAT(ROUND(SUM(IF(c.isEndedClass,dCount,0))/SUM(IF(c.isEndedClass,evaluateTotalNum,0))*100,2),\'%\'),\'0.00%\') as badEvaluateRateOfEndedClass \nFROM (\n	SELECT \n		unit.id AS unitId,\n		unit. NAME AS unitName, \n		pm.id AS managerId,\n		pm.true_name AS trueName, \n		dt.name as departmentName, \n		COUNT(DISTINCT ri.id) AS requireTotal,\n                ri.create_time AS riCreateTime,\n		COUNT(DISTINCT IF(ec.code = \'1\',ri.id,NULL)) AS waittingFallowUpNum, \n		COUNT(DISTINCT IF(ec.code = \'2\',ri.id,NULL)) AS fallowUpNum, \n		COUNT(DISTINCT IF(ec.code = \'3\',ri.id,NULL)) AS successfallowUpNum,\n		COUNT(DISTINCT IF(ec.code = \'4\',ri.id,NULL)) AS failurefallowUpNum,\n		COUNT(DISTINCT IF(ec.code = \'5\',ri.id,NULL)) AS extensionfallowUpNum, \n		\n		cl.id AS classId,\n		cl.start_time AS classStartTime,\n		cl.end_time AS classEndTime,\n		IF(DATEDIFF(cl.end_time, NOW()) < 0,TRUE,NULL) AS isEndedClass,\n		IF(DATEDIFF(cl.start_time, NOW()) >= 0,TRUE,NULL) AS isExpectedOpenClass,\n		IF(DATEDIFF(cl.start_time, NOW()) < 0 & DATEDIFF(cl.end_time, NOW()) > 0,TRUE,NULL) AS isTrainingClass, \n		DATEDIFF(cl.end_time,cl.start_time) AS daysPeriodOfClass,\n		COUNT(DISTINCT stu.id) AS studentNumOfClass, \n		IF(pi.prepareCode = \'1\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareBeforeTraining, \n		IF(pi.prepareCode = \'2\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareTraining, \n		IF(pi.prepareCode = \'3\' & pi.isPrepare = \'1\' ,TRUE,NULL) AS isPrepareAfterTraining, \n\n		COUNT(DISTINCT IF(oe.answerCode IS NOT NULL,oe.oeaId,NULL)) as evaluateTotalNum, \n		COUNT(DISTINCT IF(oe.answerCode=\'0\',oe.oeaId,NULL)) AS dCount,\n		COUNT(DISTINCT IF(oe.answerCode=\'1\',oe.oeaId,NULL)) AS cCount,\n		COUNT(DISTINCT IF(oe.answerCode=\'2\',oe.oeaId,NULL)) AS bCount, \n		COUNT(DISTINCT IF(oe.answerCode=\'3\',oe.oeaId,NULL)) AS aCount \n	FROM \n		pe_manager pm\n	INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id  \n	LEFT JOIN enum_const dt ON dt.id = pm.flag_department_type\n	INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND fk_class_master_id = pm.id  \n	INNER JOIN requirement_info ri on ri.fk_create_user_id=pm.id \n	INNER JOIN enum_const ec ON ec.id = ri.flag_follow_up_status \n	LEFT JOIN pe_student stu ON stu.fk_class_id = cl.id \n\n	LEFT JOIN (\n		SELECT tpi.fk_manager_id AS managerId, tpi.fk_class_id AS classId,pt.code AS prepareCode,tpi.is_prepare AS isPrepare\n		FROM prepare_item pi\n		INNER JOIN enum_const pt ON pt.id = pi.flag_prepare_type \n		LEFT JOIN teacher_prepare_item tpi ON tpi.fk_prepare_item_id = pi.id\n		WHERE pi.site_code = \'${siteCode}\'\n	) pi ON pi.managerId = pm.id AND pi.classId = cl.id\n\n	LEFT JOIN (\n		SELECT oea.fk_sso_user_id AS ssoUserId,ao.code AS answerCode,oea.id AS oeaId\n		FROM overall_evaluate oe\n		INNER JOIN enum_const va ON va. CODE = \'1\' AND oe.flag_isvalid = va.id  \n		INNER JOIN overall_evaluate_option oeo ON oeo.fk_evaluate_id = oe.id\n		INNER JOIN enum_const ot ON ot.code=\'2\' AND oeo.flag_option_type = ot.id \n		INNER JOIN enum_const qt ON qt.code=\'1\' AND oeo.flag_question_type = qt.id   \n		LEFT JOIN overall_evaluate_answer oea ON oea.fk_option_id = oeo.id\n		INNER JOIN enum_const ao ON ao.id = oea.answer\n		WHERE	oe.site_code = \'${siteCode}\' \n	) oe ON oe.ssoUserId = stu.fk_sso_user_id\n	WHERE\n                [ConditionSign] \n		AND  [unitId|unit.id] \n		AND  pm.site_code = \'${siteCode}\' \n	GROUP BY cl.id  \n) c\nGROUP BY c.managerId', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
