UPDATE `data_board` SET `module_data_sql`='SELECT                                                      \r\n 	count(1) as count                                \r\n FROM                                                        \r\n 	check_flow cf                                            \r\n INNER JOIN enum_const st ON st.id = cf.flag_check_status    \r\n INNER JOIN enum_const ac ON ac.id = cf.flag_active          \r\n INNER JOIN enum_const ty on ty.id = cf.flag_flow_type       \r\n WHERE                                                       \r\n 	st.code = \'1\'                                            \r\n AND (\r\n	cf.auditors LIKE \'%[peManager.id]%\'\r\n	OR cf.auditors LIKE \'%[pePriRole.id]%\'\r\n )\r\n AND (cf.scope_id is null or [peUnit|cf.scope_id])'  WHERE (`id`='14b658ad-3314-11e8-a37e-fcaa140ebf84');