INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classMasterClassManage', '班主任班级管理', '1', '1', '1', '1', '1', '1', '0', 'com.whaty.domain.bean.PeClass', NULL, '1', '', '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	c.id as id,\n        un.name as unitName,\n	c.name as `className`,\n	m.true_name as classMaster,\n	ifnull(studentNum, 0) as studentNum,\n	ifnull(courseNum, 0) as courseNum,\n	ifnull(attachNum, 0) as attachNum,\n	ifnull(signNum, 0) as signNum,\n	ifnull(homeworkNum, 0) as homeworkNum,\n	ifnull(voteNum, 0) as voteNum,\n	ifnull(questionNum, 0) as questionNum,\n	date_format(c.start_time, \'%Y年%m月%d日\') as startTime,\n	date_format(c.end_time, \'%Y年%m月%d日\') as endTime\nFROM\n	pe_class c\nINNER JOIN pe_unit un on un.id = c.fk_unit_id\nINNER JOIN pe_manager m on m.id = c.fk_class_master_id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(af.id) AS attachNum\n	FROM\n		pe_class c\n	INNER JOIN attach_file af ON af.link_id = c.id\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n	 m.fk_sso_user_id = \'${currentUserId}\'\n	AND c.site_code = \'${siteCode}\'\n        GROUP BY c.id\n) af ON af.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(stu.id) AS studentNum\n	FROM\n		pe_student stu\n	INNER JOIN pe_class c ON c.id = stu.fk_class_id\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n	 m.fk_sso_user_id = \'${currentUserId}\'\n	AND c.site_code = \'${siteCode}\' \n        GROUP BY c.id\n) stu ON stu.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(cc.id) AS courseNum\n	FROM\n		class_course cc\n	INNER JOIN pe_class c ON c.id = cc.fk_class_id\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n	 m.fk_sso_user_id = \'${currentUserId}\'\n	AND c.site_code = \'${siteCode}\' \n        GROUP BY c.id\n) cc ON cc.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(cs.id) AS signNum\n	FROM\n		class_sign cs\n	INNER JOIN pe_class c ON c.id = cs.fk_class_id\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n		c.site_code = \'${siteCode}\'\n	AND m.fk_sso_user_id = \'${currentUserId}\'\n        GROUP BY c.id\n) cs ON cs.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(hg.id) AS homeworkNum\n	FROM\n		homework_group AS hg\n	INNER JOIN pe_class c ON c.id = hg.item_id\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n		c.site_code = \'${siteCode}\'\n	AND m.fk_sso_user_id = \'${currentUserId}\'\n	AND is_delete = \'0\' \n        GROUP BY c.id\n) hg ON hg.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		sum(IF(ty. CODE = \'1\', 1, 0)) AS questionNum,\n		sum(IF(ty. CODE = \'2\', 1, 0)) AS voteNum\n	FROM\n		questionnaire_group g\n	INNER JOIN pe_class c ON c.id = g.item_id\n	INNER JOIN enum_const ty ON ty.id = g.flag_questionnaire_group_type\n	INNER JOIN enum_const pub ON pub.id = g.flag_is_publish\n	INNER JOIN pe_manager m on m.id = c.fk_class_master_id\n	WHERE\n		pub. CODE = \'1\'\n	AND g.is_delete = \'0\'\n	AND c.site_code = \'${siteCode}\'\n	AND m.fk_sso_user_id = \'${currentUserId}\'\n        GROUP BY c.id\n) g ON g.id = c.id\nWHERE\n m.fk_sso_user_id = \'${currentUserId}\'', NULL, NULL, NULL, NULL, NULL, '0', NULL, NULL, '4028ae316a09ee85016a09f4d7640000', '1', NULL, NULL, NULL);
