INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classActivityGrid', '班级活动详情', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	cs.id AS id,\n	concat(\n		cs.sign_date,\n		\' \',\n		cs.start_time,\n		\'-\',\n		cs.end_time\n	) AS signTime,\n	count(stu.id) AS willReach,\n	count(css.id) - sum(IF(iob. CODE = \'1\', 1, 0)) AS reached,\n	sum(IF(iob. CODE = \'1\', 1, 0)) AS onBehalf,\n	count(stu.id) - count(css.id) AS noReach,\n	sum(IF(ila. CODE = \'1\', 1, 0)) AS isLate,\n	sum(IF(ile. CODE = \'1\', 1, 0)) AS isLeaveEarly,\n	sum(IF(isc. CODE = \'1\', 1, 0)) AS isCribbing\nFROM\n	class_sign cs\nINNER JOIN pe_class c ON c.id = cs.fk_class_id\nINNER JOIN pe_student stu ON stu.fk_class_id = c.id\nLEFT JOIN class_sign_student css ON css.fk_class_sign_id = cs.id\nAND css.fk_student_id = stu.id\nLEFT JOIN enum_const ila ON ila.id = css.flag_is_late\nLEFT JOIN enum_const isc ON isc.id = css.flag_is_cribbing\nLEFT JOIN enum_const ile ON ile.id = css.flag_leave_early\nLEFT JOIN enum_const iob ON iob.id = css.flag_is_on_behalf\nWHERE\n	[parentId|c.id]\nAND [peUnit|c.fk_unit_id]\nGROUP BY\n	cs.id', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classAnalyse-classPieDetail', '带班统计详情-带班统计', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT \n	c.unitId AS unitId,\n	c.unitName AS unitName,\n        c.classTotalNum AS classTotalNum,\n	c.expectedOpenClassNum AS expectedOpenClassNum,\n        c.trainingClassNum AS trainingClassNum,\n	c.endedClassNum AS endedClassNum,\n	c.studentTotalNum AS studentTotalNum,\n	c.evaluateTotalNum AS evaluateTotalNumOfEndedClass,\n	c.aCount AS excellentEvaluateTotalNumOfEndedClass,\n	c.dCount AS badEvaluateTotalNumOfEndedClass,\n	IFNULL(CONCAT(ROUND(c.evaluateTotalNum/c.studentTotalNumOfEndedClass*100,2),\'%\'),\'0.00%\') as evaluateRateOfEndedClass,\n	IFNULL(CONCAT(ROUND(c.aCount/c.evaluateTotalNum*100,2),\'%\'),\'0.00%\') as excellentEvaluateRateOfEndedClass, \n	IFNULL(CONCAT(ROUND(c.dCount/c.evaluateTotalNum*100,2),\'%\'),\'0.00%\') as badEvaluateRateOfEndedClass \nFROM (\n	SELECT \n		unit.id AS unitId,\n		unit. NAME AS unitName,\n                COUNT(DISTINCT cl.id) AS classTotalNum,\n		COUNT(DISTINCT IF(DATEDIFF(cl.start_time, NOW()) >= 0,cl.id,NULL)) AS expectedOpenClassNum, -- 未开始\n		COUNT(DISTINCT IF(DATEDIFF(cl.start_time, NOW()) < 0 & DATEDIFF(cl.end_time, NOW()) > 0,cl.id,NULL)) AS trainingClassNum, -- 进行中\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0,cl.id,NULL)) AS endedClassNum, -- 已结束\n		COUNT(DISTINCT stu.id) AS studentTotalNum, -- 学员数\n                COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0,stu.id,NULL)) AS studentTotalNumOfEndedClass,\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode IS NOT NULL,oe.oeaId,NULL)) as evaluateTotalNum, \n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'0\',oe.oeaId,NULL)) AS dCount,\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'1\',oe.oeaId,NULL)) AS cCount, \n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'2\',oe.oeaId,NULL)) AS bCount,\n		COUNT(DISTINCT IF(DATEDIFF(cl.end_time, NOW()) < 0 & oe.answerCode=\'3\',oe.oeaId,NULL)) AS aCount \n	FROM \n		pe_manager pm\n	INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id \n	INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND cl.fk_class_master_id = pm.id \n	LEFT JOIN pe_student stu ON stu.fk_class_id = cl.id \n	LEFT JOIN (\n		SELECT oea.fk_sso_user_id AS ssoUserId,ao.code AS answerCode,oea.id AS oeaId\n		FROM overall_evaluate oe\n		INNER JOIN enum_const va ON va. CODE = \'1\' AND oe.flag_isvalid = va.id  \n		INNER JOIN overall_evaluate_option oeo ON oeo.fk_evaluate_id = oe.id\n		INNER JOIN enum_const ot ON ot.code=\'2\' AND oeo.flag_option_type = ot.id \n		INNER JOIN enum_const qt ON qt.code=\'1\' AND oeo.flag_question_type = qt.id  \n		LEFT JOIN overall_evaluate_answer oea ON oea.fk_option_id = oeo.id\n		INNER JOIN enum_const ao ON ao.id = oea.answer \n		WHERE	oe.site_code = \'${siteCode}\' \n	) oe ON oe.ssoUserId = stu.fk_sso_user_id\n	WHERE \n                         [analyse_dateLimit|cl.create_date]\n		AND [unit|cl.fk_unit_id]\n		AND [peUnit|cl.fk_unit_id]\n		AND pm.site_code = \'${siteCode}\' \n	GROUP BY unit.id \n )  c', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classAnalyse_classDetailAnalyse', '班级统计-班级详情列表-进行中', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	c.id as id,\n	c.name as `className`,\n	m.true_name as classMaster,\n	ifnull(studentNum, 0) as studentNum,\n	ifnull(courseNum, 0) as courseNum,\n	ifnull(attachNum, 0) as attachNum,\n	ifnull(signNum, 0) as signNum,\n	ifnull(homeworkNum, 0) as homeworkNum,\n	ifnull(voteNum, 0) as voteNum,\n	ifnull(questionNum, 0) as questionNum,\n	date_format(c.end_time, \'%Y年%m月%d日\') as endTime\nFROM\n	pe_class c\nINNER JOIN pe_manager m on m.id = c.fk_class_master_id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(af.id) AS attachNum\n	FROM\n		pe_class c\n	INNER JOIN attach_file af ON af.link_id = c.id\n	WHERE\n		[unit|c.fk_unit_id]\n	AND [peUnit |c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\'\n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) af ON af.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(stu.id) AS studentNum\n	FROM\n		pe_student stu\n	INNER JOIN pe_class c ON c.id = stu.fk_class_id\n	WHERE\n		[unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\' \n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) stu ON stu.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(cc.id) AS courseNum\n	FROM\n		class_course cc\n	INNER JOIN pe_class c ON c.id = cc.fk_class_id\n	WHERE\n		[unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\' \n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) cc ON cc.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(cs.id) AS signNum\n	FROM\n		class_sign cs\n	INNER JOIN pe_class c ON c.id = cs.fk_class_id\n	WHERE\n		c.site_code = \'${siteCode}\'\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) cs ON cs.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(hg.id) AS homeworkNum\n	FROM\n		homework_group AS hg\n	INNER JOIN pe_class c ON c.id = hg.item_id\n	WHERE\n		c.site_code = \'${siteCode}\'\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND is_delete = \'0\' \n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) hg ON hg.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		sum(IF(ty. CODE = \'1\', 1, 0)) AS questionNum,\n		sum(IF(ty. CODE = \'2\', 1, 0)) AS voteNum\n	FROM\n		questionnaire_group g\n	INNER JOIN pe_class c ON c.id = g.item_id\n	INNER JOIN enum_const ty ON ty.id = g.flag_questionnaire_group_type\n	INNER JOIN enum_const pub ON pub.id = g.flag_is_publish\n	WHERE\n		pub. CODE = \'1\'\n	AND g.is_delete = \'0\'\n	AND c.site_code = \'${siteCode}\'\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND [analyse_dateLimit|c.create_date]\n        AND [className_like|c.name]\n        AND c.end_time >= now() AND c.start_time <= now()\n        GROUP BY c.id\n) g ON g.id = c.id\nWHERE\n	[unit|c.fk_unit_id]\nAND [peUnit|c.fk_unit_id]\nAND c.site_code = \'${siteCode}\'\nAND [analyse_dateLimit|c.create_date]\nAND [className_like|c.name]\nAND c.end_time >= now() AND c.start_time <= now()', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classAnalyse_classDetailAnalyse_isEnd', '班级统计-班级详情统计-已结束', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	c.id AS id,\n	c. NAME AS className,\n        m.true_name as classMasterName,\n	ifnull(\n		oeo.`group` + oeo.classMaster + oeo.rearService + cou.course,\n		0\n	) AS total,\n	ifnull(oeo.`group`, 0) AS `group`,\n	ifnull(oeo.classMaster, 0) AS classMaster,\n	ifnull(oeo.rearService, 0) AS rearService,\n	ifnull(cou.course, 0) AS course,\n	stu.studentNumber AS studentNumber,\n	ifnull(cs.signCount, 0) as signCount,\n	concat(round(ifnull(cs.signNumber, 0) / (stu.studentNumber * ifnull(cs.signCount, 1)) * 100, 2), \'%\') as signRate\nFROM\n	pe_class c\nINNER JOIN pe_manager m on m.id = c.fk_class_master_id\nLEFT JOIN (\n	SELECT\n		cl.id AS id,\n		round(\n			sum(ao. CODE + 1) / count(DISTINCT stu.id),\n			1\n		) AS `course`\n	FROM\n		overall_evaluate_answer oea\n	INNER JOIN pe_course c ON c.id = oea.rel_id\n	INNER JOIN enum_const ao ON ao.id = oea.answer_id\n	INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id\n	INNER JOIN pe_class cl ON cl.id = stu.fk_class_id\n	WHERE\n		[analyse_dateLimit|c.create_date]\n	AND [unit|cl.fk_unit_id]\n	AND [peUnit|cl.fk_unit_id]\n	AND cl .site_code = \'${siteCode}\'\n        AND cl .end_time < now()\n	GROUP BY\n		cl.id\n) cou ON cou.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		round(\n			sum(\n\n				IF (ot. CODE = \'1\', ao. CODE + 1, 0)\n			) / count(DISTINCT stu.id),\n			1\n		) AS `group`,\n		round(\n			sum(\n\n				IF (ot. CODE = \'2\', ao. CODE + 1, 0)\n			) / count(DISTINCT stu.id),\n			1\n		) AS `classMaster`,\n		round(\n			sum(\n\n				IF (ot. CODE = \'3\', ao. CODE + 1, 0)\n			) / count(DISTINCT stu.id),\n			1\n		) AS `rearService`\n	FROM\n		overall_evaluate_option oeo\n	INNER JOIN overall_evaluate_answer oea ON oeo.id = oea.fk_option_id\n	INNER JOIN enum_const ot ON ot.id = oeo.flag_option_type\n	INNER JOIN enum_const ao ON ao.id = oea.answer_id\n	INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id\n	INNER JOIN pe_class c ON c.id = stu.fk_class_id\n	WHERE\n	[analyse_dateLimit|c.create_date]\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\'\n        AND c.end_time < now()\n	GROUP BY\n		c.id\n) oeo ON oeo.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(stu.id) studentNumber\n	FROM\n		pe_student stu\n	INNER JOIN pe_class c ON c.id = stu.fk_class_id\n	WHERE\n	[analyse_dateLimit|c.create_date]\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\'\n        AND c.end_time < now()\n	GROUP BY\n		stu.fk_class_id\n) stu ON stu.id = c.id\nLEFT JOIN (\n	SELECT\n		cs.fk_class_id AS id,\n		count(DISTINCT cs.id) as signCount,\n		count(css.id) AS signNumber\n	FROM\n		class_sign cs\n	INNER JOIN class_sign_student css ON css.fk_class_sign_id = cs.id\n	INNER JOIN pe_class c ON c.id = cs.fk_class_id\n	WHERE\n	[analyse_dateLimit|c.create_date]\n	AND [unit|c.fk_unit_id]\n	AND [peUnit|c.fk_unit_id]\n	AND c.site_code = \'${siteCode}\'\n        AND c.end_time < now()\n	GROUP BY\n		cs.fk_class_id\n) cs on cs.id = c.id\nWHERE\n	[analyse_dateLimit|c.create_date]\nAND [unit|c.fk_unit_id]\nAND [peUnit|c.fk_unit_id]\nAND c.site_code = \'${siteCode}\'\nAND c.end_time < now()', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classAnalyse_classDetailAnalyse_noStart', '班级统计-班级详情统计-未开始', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	c.id as id,\n	c.name as className,\n	m.true_name as classMaster,\n	ifnull(studentNumber, 0) as studentNumber,\n	ifnull(courseNumber, 0) as courseNumber,\n	ifnull(attachFileNumber, 0) as attachFileNumber,\n	date_format(c.start_time, \'%Y年%m月%d日\') as startTime,\n	date_format(c.end_time, \'%Y年%m月%d日\') as endTime,\n	c.training_day_number as trainingDay\nFROM\n	pe_class c\nINNER JOIN pe_manager m ON m.id = c.fk_class_master_id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(stu.id) AS studentNumber\n	FROM\n		pe_student stu\n	INNER JOIN pe_class c ON c.id = stu.fk_class_id\n	GROUP BY\n		c.id\n) stu ON stu.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(cc.id) AS courseNumber\n	FROM\n		class_course cc\n	INNER JOIN pe_class c ON c.id = cc.fk_class_id\n	GROUP BY\n		c.id\n) course on course.id = c.id\nLEFT JOIN (\n	SELECT\n		c.id AS id,\n		count(af.id) AS attachFileNumber\n	FROM\n		attach_file af\n	INNER JOIN pe_class c ON c.id = af.link_id\n	GROUP BY\n		c.id\n) af ON af.id = c.id\nwhere\n	[analyse_dateLimit|c.create_date]\nAND [unit|c.fk_unit_id]\nAND [peUnit|c.fk_unit_id]\nAND c.site_code = \'${siteCode}\'', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classQualityDetailGrid', '班级质量评估-组织管理', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT oeo.id AS id, oeo. NAME AS optionName, cast( concat( COUNT(IF(ao. CODE = \'0\', 1, NULL)), \'(\', concat( round( COUNT(IF(ao. CODE = \'0\', 1, NULL)) / COUNT(stu.id) * 100, 2 ), \'%\' ), \')\' ) AS CHAR ) AS best, cast( concat( COUNT(IF(ao. CODE = \'1\', 1, NULL)), \'(\', concat( round( COUNT(IF(ao. CODE = \'1\', 1, NULL)) / COUNT(stu.id) * 100, 2 ), \'%\' ), \')\' ) AS CHAR ) AS good, cast( concat( COUNT(IF(ao. CODE = \'2\', 1, NULL)), \'(\', concat( round( COUNT(IF(ao. CODE = \'2\', 1, NULL)) / COUNT(stu.id) * 100, 2 ), \'%\' ), \')\' ) AS CHAR ) AS general, cast( concat( COUNT(IF(ao. CODE = \'3\', 1, NULL)), \'(\', concat( round( COUNT(IF(ao. CODE = \'3\', 1, NULL)) / COUNT(stu.id) * 100, 2 ), \'%\' ), \')\' ) AS CHAR ) AS bad FROM pe_class cl INNER JOIN pe_student stu ON stu.fk_class_id = cl.id INNER JOIN enum_const qt ON qt.NAMESPACE = \'flagQuestionType\' AND qt. CODE = \'1\' INNER JOIN enum_const va ON va.namespace = \'flagIsvalid\' AND va. CODE = \'1\' INNER JOIN overall_evaluate oe ON oe.flag_isvalid = va.id INNER JOIN overall_evaluate_option oeo ON oeo.fk_evaluate_id = oe.id AND oeo.flag_question_type = qt.id INNER JOIN enum_const ot ON ot.id = oeo.flag_option_type LEFT JOIN overall_evaluate_answer oea ON oea.fk_sso_user_id = stu.fk_sso_user_id AND oea.fk_option_id = oeo.id LEFT JOIN enum_const ao ON ao.id = oea.answer WHERE ot. CODE IN (\'1\', \'2\') AND [parentId|cl.id] GROUP BY oeo.id', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', 'dialog', NULL, NULL);
INSERT INTO `grid_basic_config` (`id`, `title`, `can_search`, `can_add`, `can_delete`, `can_update`, `can_batch_add`, `can_projections`, `first_search`, `entity_class`, `note`, `list_type`, `dc`, `flag_active`, `flag_bak`, `dateFormat`, `sqlstr`, `statistics`, `s_note`, `update_note`, `is_enum`, `countsql`, `isGroupSql`, `LIST_FUNCTION`, `FLAG_INTE_ACTIVE`, `fk_web_site_id`, `can_checked_all`, `add_mode`, `add_cmp_name`, `fk_chart_id`) VALUES ('classQualityDetailGridCourse', '班级质量评估-课程评价', '0', NULL, NULL, NULL, NULL, '0', '0', NULL, NULL, '1', NULL, '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, 'SELECT\n	cou.id AS id,\n	cou. NAME AS optionName,\n	cast(\n		concat(\n			COUNT(IF(ao. CODE = \'0\', 1, NULL)),\n			\'(\',\n			concat(\n				round(\n					COUNT(IF(ao. CODE = \'0\', 1, NULL)) / COUNT(stu.id) * 100,\n					2\n				),\n				\'%\'\n			),\n			\')\'\n		) AS CHAR\n	) AS best,\n	cast(\n		concat(\n			COUNT(IF(ao. CODE = \'1\', 1, NULL)),\n			\'(\',\n			concat(\n				round(\n					COUNT(IF(ao. CODE = \'1\', 1, NULL)) / COUNT(stu.id) * 100,\n					2\n				),\n				\'%\'\n			),\n			\')\'\n		) AS CHAR\n	) AS good,\n	cast(\n		concat(\n			COUNT(IF(ao. CODE = \'2\', 1, NULL)),\n			\'(\',\n			concat(\n				round(\n					COUNT(IF(ao. CODE = \'2\', 1, NULL)) / COUNT(stu.id) * 100,\n					2\n				),\n				\'%\'\n			),\n			\')\'\n		) AS CHAR\n	) AS general,\n	cast(\n		concat(\n			COUNT(IF(ao. CODE = \'3\', 1, NULL)),\n			\'(\',\n			concat(\n				round(\n					COUNT(IF(ao. CODE = \'3\', 1, NULL)) / COUNT(stu.id) * 100,\n					2\n				),\n				\'%\'\n			),\n			\')\'\n		) AS CHAR\n	) AS bad\nFROM\n	pe_class cl\nINNER JOIN pe_student stu ON stu.fk_class_id = cl.id\nINNER JOIN class_course cc ON cc.fk_class_id = cl.id\nINNER JOIN pe_course cou ON cou.id = cc.fk_course_id\nLEFT JOIN overall_evaluate_answer oea ON oea.fk_sso_user_id = stu.fk_sso_user_id\nAND oea.rel_id = cou.id\nLEFT JOIN enum_const ao ON ao.id = oea.answer\nWHERE\n	[parentId|cl.id]\nGROUP BY\n	cou.id', NULL, NULL, NULL, NULL, NULL, NULL, '0', NULL, '4028ae316a09ee85016a09f4d7640000', '0', NULL, NULL, NULL);
