INSERT INTO `analyse_config_detail` VALUES ('0a18c5a3157511eabd12fcaa140ebf84', '{\r\n	\"sql\": \"select ty.name as courseType, count(c.id) as `value` from pe_course c inner join enum_const ty on ty.id = c.flag_course_type where c.site_code = \'${siteCode}\' and [items|ty.name] group by ty.id\",\r\n	\"item\": {\r\n		\"alias\": \"courseType\",\r\n		\"xItemSql\": \"select name from enum_const where namespace = \'flagCourseType\' and TEAM like \'%,${siteCode},%\'\"\r\n	},\r\n	\"isReverse\": true,\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"type\": \"bar\",\r\n			\"valueAxisKey\": \"courseType\"\r\n		}\r\n	]},\r\n	\"axis\": [\r\n		{\r\n			\"key\": \"courseType\",\r\n			\"serial\": 1\r\n		}\r\n	]\r\n}', '04cff8f2157511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('0bee31331b1f11eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"sql\":\"SELECT un. NAME AS `name`, round(sum(training_total_fee) / 10000, 2 ) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id ORDER BY sum(training_total_fee) DESC LIMIT 0, 10\",\r\n	\"innerContentSql\": \"SELECT cast(concat(round(sum(training_total_fee) / 10000, 2 ), \'万元\') as char) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id]\"\r\n}', '0bee31331b1f11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('0efed3581a6a11eabd12fcaa140ebf84', '{\"sql\": [\"SELECT concat( round( count(an.id) / count(stu.id) * 100, 1 ), \'%\' ) AS avgRate, concat( count(an.id) / count(DISTINCT stu.id), \'次\' ) AS avg, cast( concat( count(an.id), \'/\', count(stu.id), \'次\' ) AS CHAR ) AS complete FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN questionnaire_group hg ON hg.item_id = c.id INNER JOIN questionnaire_member mem ON mem.fk_group_id = hg.id AND mem.fk_sso_user_id = stu.fk_sso_user_id INNER JOIN enum_const ty ON ty.id = hg.flag_questionnaire_group_type LEFT JOIN questionnaire_answer an ON an.fk_group_id = hg.id AND an.fk_member_id = mem.id WHERE c.site_code = \'${siteCode}\' AND ty. CODE = \'2\' AND [parentId|c.id] AND hg.end_time < now() AND hg.is_delete = \'0\' AND [peUnit|c.fk_unit_id] AND [unit|c.fk_unit_id] AND [analyse_dateLimit|c.create_date]\"], \"items\": [{\"alias\": \"avgRate\", \"label\": \"平均回收率\", \"serial\": 1}, {\"alias\": \"avg\", \"label\": \"平均投票次数\", \"serial\": 2}, {\"alias\": \"complete\", \"label\": \"回收人次\", \"serial\": 3}]}', '0efed3581a6a11eabd12fcaa140ebf84');
