INSERT INTO `analyse_config_detail` VALUES ('11dff106127c11eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT date_Format( ti.training_start_time, \'${dateFormat}\' ) AS xAxis, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND ti.site_code = \'${siteCode}\' AND [items|date_Format(ti.training_start_time, \'${dateFormat}\')] GROUP BY date_Format( ti.training_start_time, \'${dateFormat}\' )\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"axis\": [{name: \"项目数\", \"key\": \"a1\", \"serial\": 1, \"unit\": \"个\"}, {name: \"项目数2\", \"key\": \"a2\", \"serial\": 2}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"isEnd\",\r\n			\"name\": \"已结束\",\r\n			\"hasAverageLine\": true,\r\n			\"type\": \"bar\",\r\n			\"valueAxisKey\": \"a1\"\r\n		}, {\r\n			\"alias\": \"noStart\",\r\n			\"name\": \"未开始\",\r\n			\"hasAverageLine\": false,\r\n			\"type\": \"bar\"\r\n		}, {\r\n			\"alias\": \"inProgress\",\r\n			\"name\": \"进行中\",\r\n			\"type\": \"line\",\r\n			\"hasAverageLine\": true,\r\n			\"valueAxisKey\": \"a2\"\r\n		}\r\n	]},\r\n\"columnPanel\": {\"sql\":\"SELECT round(sum(c.training_total_fee)/10000,2) AS incoming,round(sum(c.teacher_fee+c.teaching_fee+c.transport_fee+c.food_fee+c.room_fee+c.enroll_fee+c.material_fee+c.site_fee+c.period_fee+c.other_fee)/10000,2) AS pay,round((sum(c.training_total_fee)-sum(c.teacher_fee+c.teaching_fee+c.transport_fee+c.food_fee+c.room_fee+c.enroll_fee+c.material_fee+c.site_fee+c.period_fee+c.other_fee))/10000,2) AS balanch FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id=c.id INNER JOIN enum_const ty ON ty.id=cf.flag_flow_type INNER JOIN enum_const ss ON ss.id=c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id=cf.flag_active INNER JOIN enum_const cs ON cs.id=cf.flag_check_status WHERE ty.CODE=\'4\' AND ss.CODE=\'2\' AND ac.CODE=\'1\' AND cs.CODE=\'2\'\",\r\n	\"columns\":[{\"alias\":\"incoming\",\"label\":\"总收入\",\"serial\":1,\"highlight\": false},{\"alias\": \"pay\",\"label\": \"总支出\",\"serial\":2,\"highlight\": false},{\"alias\": \"balanch\",\"label\": \"结余\",\"serial\": 3,\"highlight\": false}]}\r\n}', '0d65a97e127c11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('15a80b5a1bb411eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT cast(followNum as char) AS `xAxis`, round( sum(IF(st. CODE = \'3\', 1, 0)) / sum(IF(st. CODE IN(\'3\', \'4\'), 1, 0)) * 100, 2 ) AS `successPercent` FROM requirement_info ri INNER JOIN ( SELECT ri.id AS fk_requirement_info_id, count(rfui.id) AS followNum FROM requirement_follow_up_info rfui INNER JOIN requirement_info ri ON ri.id = rfui.fk_requirement_info_id WHERE [analyse_dateLimit|ri.create_time] AND ri.site_code = \'${siteCode}\' GROUP BY ri.id ) rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND ri.site_code = \'${siteCode}\' GROUP BY followNum\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.BusinessGenerator\",\r\n		\"staticMethodName\": \"requirementFollowNum\"\r\n	},\r\n	\"axis\": [{name: \"成功率\", \"unit\": \"%\"}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"successPercent\",\r\n			\"name\": \"成功率\",\r\n			\"type\": \"line\"\r\n		}\r\n	]}\r\n}', '15a80b5a1bb411eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('1ab55935102811eabd12fcaa140ebf84', '{\"label\": \"质量评估\", \"sql\": [\"select 100 as compare, 57 as basic\"], \"basicItem\": {\"label\": \"平均值\", alias: \"basic\"}, \"compareItem\": {\"label\": \"当前值\", \"alias\": \"compare\"}}', '0d4e2a7d102811eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('1ba5dac317f611eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT un. NAME as \'xAxis\', sum(ao. CODE + 1) AS `value` FROM overall_evaluate_answer oea INNER JOIN enum_const ao ON ao.id = oea.answer_id INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN pe_unit un ON un.id = c.fk_unit_id WHERE [analyse_dateLimit|oea.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\' GROUP BY c.fk_unit_id ORDER BY `value` DESC\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"xItemSql\": \"SELECT un. NAME as `name` FROM overall_evaluate_answer oea INNER JOIN enum_const ao ON ao.id = oea.answer_id INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN pe_unit un ON un.id = c.fk_unit_id WHERE [analyse_dateLimit|oea.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\' GROUP BY c.fk_unit_id ORDER BY sum(ao. CODE) DESC\"\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"name\": \"质量评估\",\r\n			\"type\": \"bar\"\r\n		}\r\n	]}\r\n}', '143ed7ea17f611eabd12fcaa140ebf84');
