INSERT INTO `analyse_config_detail` VALUES ('1c624da11bb711eabd12fcaa140ebf84', '{\r\n	\"isReverse\": true,\r\n	\"sql\": \"SELECT \'目标客户\' AS xAxis, count(ri.id) AS `value` FROM requirement_info ri INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND ri.site_code = \'${siteCode}\' UNION ALL SELECT \'潜在客户\' AS xAxis, count(ri.id) - sum(IF(st. CODE = \'1\', 1, 0)) AS `value` FROM requirement_info ri INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND ri.site_code = \'${siteCode}\' UNION ALL SELECT \'意向客户\' AS xAxis, sum(IF(st. CODE = \'2\', 1, 0)) + sum(IF(st. CODE = \'3\', 1, 0)) + sum(IF(st. CODE = \'5\', 1, 0)) AS `value` FROM requirement_info ri INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND ri.site_code = \'${siteCode}\' UNION ALL SELECT \'成交客户\' AS xAxis, sum(IF(st. CODE = \'3\', 1, 0)) AS `value` FROM requirement_info ri INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND ri.site_code = \'${siteCode}\'\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"items\": [\"成交客户\", \"意向客户\", \"潜在客户\", \"目标客户\"]\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"name\": \"需求数\",\r\n			\"type\": \"bar\"\r\n		}\r\n	]}\r\n}', '1c624da11bb711eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('20876fbb17f911eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(ti.id) AS total, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id AND [peUnit|un.id] INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND [un|un.id] AND [date_dateLimit|ti.training_start_time] AND ti.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"培训项目\", \"items\": [{\"alias\": \"total\", \"label\": \"项目总数\", \"serial\": 1}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 4}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 5}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 6}]}', '1ab0428717f911eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('2344ee021b2f11eabd12fcaa140ebf84', '{\r\n	\"unit\": \"个\",\r\n	\"showLegend\": true,\r\n	\"isMapMetaData\": true,\r\n	\"sql\":\"SELECT sum(IF(ty. CODE = \'1\', 1, 0)) AS noFollow, sum(IF(ty. CODE = \'2\', 1, 0)) AS following, sum(IF(ty. CODE = \'3\', 1, 0)) AS signed, sum(IF(ty. CODE = \'4\', 1, 0)) AS failure, sum(IF(ty. CODE = \'5\', 1, 0)) AS delay FROM requirement_info ri INNER JOIN requirement_follow_up_info rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN enum_const ty ON ty.id = ri.flag_follow_up_status INNER JOIN pe_manager m ON m.id = ri.fk_follow_up_user_id WHERE [unit|m.fk_unit_id] AND [peUnit|m.fk_unit_id] AND [analyse_dateLimit|ri.create_time] AND m.site_code = \'${siteCode}\'\",\r\n	\"items\":[{\"alias\":\"noFollow\",\"name\":\"未跟进\"}, {\"alias\":\"following\",\"name\":\"跟进中\"}, {\"alias\":\"signed\",\"name\":\"已签约\"}, {\"alias\":\"failure\",\"name\":\"失败\"}, {\"alias\":\"delay\",\"name\":\"延期\"}],\r\n	\"innerPieItems\": [{\"name\": \"未跟进\", \"mappingOutItems\": [\"noFollow\"]}, {\"name\": \"已跟进\", \"mappingOutItems\": [\"following\", \"signed\", \"failure\", \"delay\"]}],\r\n	\"innerPieItems\": [{\"name\": \"未跟进\", \"mappingOutItems\": [\"noFollow\"]}, {\"name\": \"已跟进\", \"mappingOutItems\": [\"following\", \"signed\", \"failure\", \"delay\"]}],\r\n	\"columnPanel\": {\"sql\":\"SELECT count(ri.id) AS total, ifnull(count(ri.id) - sum(IF(ty. CODE = \'1\', 1, 0)), 0) AS followed, ifnull(sum(IF(ty. CODE = \'1\', 1, 0)), 0) AS noFollow FROM requirement_info ri INNER JOIN requirement_follow_up_info rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN enum_const ty ON ty.id = ri.flag_follow_up_status INNER JOIN pe_manager m ON m.id = ri.fk_follow_up_user_id WHERE [unit|m.fk_unit_id] AND [peUnit|m.fk_unit_id] AND [analyse_dateLimit|ri.create_time] AND m.site_code = \'${siteCode}\'\", \"columns\":[{\"alias\":\"total\",\"label\":\"总需求书\",\"serial\":1,\"highlight\": false}, {\"alias\":\"followed\",\"label\":\"已跟进\",\"serial\":2,\"highlight\": false}, {\"alias\":\"noFollow\",\"label\":\"未跟进\",\"serial\":3,\"highlight\": false}]}\r\n}', '2344ee021b2f11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('25da996f0da111eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(ti.id) AS total, sum(IF(ty. CODE = \'1\', 1, 0)) AS society, sum(IF(ty. CODE = \'2\', 1, 0)) AS cooperate, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id AND [peUnit|un.id] INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND [un|un.id] AND [analyse_dateLimit|ti.training_start_time] AND ti.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"培训项目\", \"columns\": [{\"alias\": \"total\", \"label\": \"项目总数\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"society\", \"label\": \"社会项目\", \"serial\": 2, \"highlight\": false}, {\"alias\": \"cooperate\", \"label\": \"委托项目\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 4, \"highlight\": true}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 5, \"highlight\": false}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 6, \"highlight\": false}]}', '0bfaa9340da111eabd12fcaa140ebf84');
