INSERT INTO `analyse_config_detail` VALUES ('2805b99c1af511eabd12fcaa140ebf84', '{\r\n	\"showLegend\": false,\r\n	\"isMapMetaData\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT count(css.id) - sum(IF(iob. CODE = \'1\', 1, 0)) AS reached, sum(IF(iob. CODE = \'1\', 1, 0)) AS onBehalf, count(stu.id) - count(css.id) AS noReach FROM class_sign cs INNER JOIN pe_class c ON c.id = cs.fk_class_id INNER JOIN pe_student stu ON stu.fk_class_id = c.id LEFT JOIN class_sign_student css ON css.fk_class_sign_id = cs.id AND css.fk_student_id = stu.id LEFT JOIN enum_const iob ON iob.id = css.flag_is_on_behalf WHERE [id|cs.id]\",\r\n  \"items\": [{\"name\": \"未签到\", \"alias\": \"noReach\"}, {\"name\": \"签到\", \"alias\": \"reached\"}, {\"name\": \"代签\", \"alias\": \"onBehalf\"}]\r\n}', '2805b99c1af511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('2a3687211a5f11eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT cs.date AS xAxis, ifnull(cs.signNum, 0) AS signNum, ifnull(hg.homeworkNum, 0) AS homeworkNum, ifnull(g.questionNum, 0) AS questionNum, ifnull(g.voteNum, 0) as voteNum FROM ( SELECT DATE_FORMAT(cs.create_date, \'%y-%m-%d\') AS date, count(cs.id) AS signNum FROM class_sign cs INNER JOIN pe_class c ON c.id = cs.fk_class_id WHERE [parentId|c.id] AND [items|DATE_FORMAT(cs.create_date, \'%y-%m-%d\')] GROUP BY DATE_FORMAT(cs.create_date, \'%y-%m-%d\')) cs LEFT JOIN ( SELECT DATE_FORMAT(hg.create_date, \'%y-%m-%d\') AS date, count(hg.id) AS homeworkNum FROM homework_group AS hg INNER JOIN pe_class c ON c.id = hg.item_id WHERE [parentId|c.id] AND is_delete = \'0\' AND [items|DATE_FORMAT(hg.create_date, \'%y-%m-%d\')] GROUP BY DATE_FORMAT(hg.create_date, \'%y-%m-%d\')) hg ON hg.date = cs.date LEFT JOIN ( SELECT DATE_FORMAT(g.create_date, \'%y-%m-%d\') AS date, sum(IF(ty. CODE = \'1\', 1, 0)) AS questionNum, sum(IF(ty. CODE = \'2\', 1, 0)) AS voteNum FROM questionnaire_group g INNER JOIN pe_class c ON c.id = g.item_id INNER JOIN enum_const ty ON ty.id = g.flag_questionnaire_group_type INNER JOIN enum_const pub ON pub.id = g.flag_is_publish WHERE pub. CODE = \'1\' AND g.is_delete = \'0\' AND [parentId|c.id] AND [items|DATE_FORMAT(g.create_date, \'%y-%m-%d\')] GROUP BY DATE_FORMAT(g.create_date, \'%y-%m-%d\')) g ON g.date = cs.date\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.BusinessGenerator\",\r\n		\"staticMethodName\": \"classActiveDayByParentId\"\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"signNum\",\r\n			\"name\": \"签到\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"activity\"\r\n		}, {\r\n			\"alias\": \"homeworkNum\",\r\n			\"name\": \"作业\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"activity\"\r\n		}, {\r\n			\"alias\": \"questionNum\",\r\n			\"name\": \"问卷\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"activity\"\r\n		}, {\r\n			\"alias\": \"voteNum\",\r\n			\"name\": \"投票\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"activity\"\r\n		}\r\n	]}\r\n}', '2a3687211a5f11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('2b95c66417d311eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(id) as classNum from pe_class where [analyse_dateLimit|create_date] AND [unit|fk_unit_id] AND [peUnit|fk_unit_id] AND site_code = \'${siteCode}\'\",\r\n\"select count(af.id) as attachNum from pe_class c inner join attach_file af on af.link_id = c.id where [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\'\",\r\n\"select count(stu.id) as studentNum from pe_student stu inner join pe_class c on c.id = stu.fk_class_id where [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\'\",\r\n\"select count(cc.id) as courseNum from class_course cc inner join pe_class c on c.id = cc.fk_class_id where [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"班级信息\", \"columns\": [{\"alias\": \"classNum\", \"label\": \"班级数量\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"studentNum\", \"label\": \"学员数量\", \"serial\": 2, \"highlight\": false}, {\"alias\": \"courseNum\", \"label\": \"课程数\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"attachNum\", \"label\": \"附件数\", \"serial\": 4, \"highlight\": false}]}', '258af62617d311eabd12fcaa140ebf84');
