INSERT INTO `analyse_config_detail` VALUES ('2f5ec5f01b2711eabd12fcaa140ebf84', '{\r\n	\"showLegend\": false,\r\n	\"isMapMetaData\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT round(sum(c.teacher_fee) / 10000, 2) AS teacherFee, round( sum(c.teaching_fee) / 10000, 2 ) AS teachingFee, round( sum(c.transport_fee) / 10000, 2 ) AS transportFee, round(sum(c.food_fee) / 10000, 2) AS foodFee, round(sum(c.room_fee) / 10000, 2) AS roomFee, round(sum(c.enroll_fee) / 10000, 2) AS enrollFee, round( sum(c.material_fee) / 10000, 2 ) AS materialFee, round(sum(c.site_fee) / 10000, 2) AS siteFee, round(sum(c.period_fee) / 10000, 2) AS periodFee, round(sum(c.other_fee) / 10000, 2) AS otherFee FROM pe_class c INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status WHERE ss. CODE = \'2\' AND [id|c.fk_unit_id]\",\r\n	\"items\":[{\"alias\":\"teacherFee\",\"name\":\"师资费\"},{\"alias\":\"teachingFee\",\"name\":\"现场教学费\"},{\"alias\":\"transportFee\",\"name\":\"交通费\"},{\"alias\":\"foodFee\",\"name\":\"餐饮费\"},{\"alias\":\"roomFee\",\"name\":\"住宿费\"},{\"alias\":\"enrollFee\",\"name\":\"报名费\"},{\"alias\":\"materialFee\",\"name\": \"教材费\"}, {\"alias\": \"siteFee\",\"name\": \"场地费\"}, {\"alias\": \"periodFee\",\"name\": \"劳务带班费\"}, {\"alias\":\"otherFee\",\"name\": \"其他费用\"}]\r\n}', '2f5ec5f01b2711eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('2fe2555817d511eabd12fcaa140ebf84', '{\"sql\": [\"select count(cs.id) as signNum from class_sign cs inner join pe_class c on c.id = cs.fk_class_id WHERE c.site_code = \'${siteCode}\' AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND [parentId|c.id] AND [analyse_dateLimit|cs.create_date]\",\r\n\"select count(hg.id) as homeworkNum from homework_group as hg inner join pe_class c on c.id = hg.item_id WHERE c.site_code = \'${siteCode}\' AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND [parentId|c.id] AND [analyse_dateLimit|hg.create_date] and is_delete = \'0\';\",\r\n\"select sum(if(ty.code = \'1\', 1, 0)) as questionNum, sum(if(ty.code = \'2\', 1, 0)) as voteNum from questionnaire_group g inner join pe_class c on c.id = g.item_id inner join enum_const ty on ty.id = g.flag_questionnaire_group_type inner join enum_const pub on pub.id = g.flag_is_publish where pub.code = \'1\' and g.is_delete = \'0\' AND [parentId|c.id] AND c.site_code = \'${siteCode}\' AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND [analyse_dateLimit|g.create_date];\"],\r\n	\"title\": \"班级活动\", \"columns\": [{\"alias\": \"signNum\", \"label\": \"签到\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"homeworkNum\", \"label\": \"作业\", \"serial\": 2, \"highlight\": false}, {\"alias\": \"questionNum\", \"label\": \"问卷调查\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"voteNum\", \"label\": \"调查问卷\", \"serial\": 4, \"highlight\": false}]}', '1d9404ec17d511eabd12fcaa140ebf84');
