INSERT INTO `analyse_config_detail` VALUES ('408844a5174011eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT date_format( ti.create_date, \'${dateFormat}\' ) AS xAxis <for items = \'sqlAlias\' element = \'alias\'>, ${alias} </for> FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_type INNER JOIN enum_const ss ON ss.id = ti.flag_item_status WHERE ss. CODE = \'3\' AND ti.site_code = \'${siteCode}\' AND [peUnit|ti.fk_unit_id] AND [unit|ti.fk_unit_id] AND [analyse_dateLimit|ti.create_date] GROUP BY date_format(ti.create_date, \'${dateFormat}\')\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"series\": {\r\n		\"sql\": \"select concat(\'type\', code) as alias, name as `name`, \'bar\' as type, concat(\'sum(if(ty.code = \'\'\', code, \'\'\', 1, 0)) as type\', code) as sqlAlias, \'type\' as `group` from enum_const where namespace = \'flagTrainingType\' and team like \'%,${siteCode},%\'\"\r\n	}\r\n}', '20594710174011eabd12fcaa140ebf84');
