INSERT INTO `analyse_config_detail` VALUES ('4c4b500a17f011eabd12fcaa140ebf84', '{\r\n	\"showLegend\": true,\r\n	\"sql\":\"select un.name as `name`, count(c.id) as `value` from pe_class c inner join pe_unit un on un.id = c.fk_unit_id where c.site_code = \'${siteCode}\' and [analyse_dateLimit|c.create_date] and [unit|c.fk_unit_id] and [peUnit|c.fk_unit_id] group by c.fk_unit_id order by count(c.id) desc limit 0, 10\",\r\n	\"innerContentSql\": \"select count(c.id) as `total` from pe_class c inner join pe_unit un on un.id = c.fk_unit_id where c.site_code = \'${siteCode}\' and [analyse_dateLimit|c.create_date] and [unit|c.fk_unit_id] and [peUnit|c.fk_unit_id]\"\r\n}', '479a4aa317f011eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('5832f6d7173411eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(ti.id) AS total, sum(IF(ty. CODE = \'1\', 1, 0)) AS society, sum(IF(ty. CODE = \'2\', 1, 0)) AS cooperate, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id AND [peUnit|un.id] INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND [unit|un.id] AND [analyse_dateLimit|ti.training_start_time] AND ti.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"培训项目\", \"columns\": [{\"alias\": \"total\", \"label\": \"项目总数\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"society\", \"label\": \"社会项目\", \"serial\": 2, \"highlight\": false}, {\"alias\": \"cooperate\", \"label\": \"委托项目\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 4, \"highlight\": true}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 5, \"highlight\": false}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 6, \"highlight\": false}]}', '26fddfc7173411eabd12fcaa140ebf84');
