INSERT INTO `analyse_config_detail` VALUES ('689b102b173d11eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"isMapMetaData\": true,\r\n	\"sql\":\"SELECT round( r.total - r.teacherFee + r.teachingFee + r.transportFee + r.foodFee + r.roomFee + r.enrollFee + r.materialFee + r.siteFee + r.periodFee + r.otherFee, 2 ) AS balance, r.teacherFee AS teacherFee, r.teachingFee AS teachingFee, r.transportFee AS transportFee, r.foodFee AS foodFee, r.roomFee AS roomFee, r.enrollFee AS enrollFee, r.materialFee AS materialFee, r.siteFee AS siteFee, r.periodFee AS periodFee, r.otherFee AS otherFee FROM ( SELECT round( sum(c.training_total_fee) / 10000, 2 ) AS total, round(sum(c.teacher_fee) / 10000, 2) AS teacherFee, round( sum(c.teaching_fee) / 10000, 2 ) AS teachingFee, round( sum(c.transport_fee) / 10000, 2 ) AS transportFee, round(sum(c.food_fee) / 10000, 2) AS foodFee, round(sum(c.room_fee) / 10000, 2) AS roomFee, round(sum(c.enroll_fee) / 10000, 2) AS enrollFee, round( sum(c.material_fee) / 10000, 2 ) AS materialFee, round(sum(c.site_fee) / 10000, 2) AS siteFee, round(sum(c.period_fee) / 10000, 2) AS periodFee, round(sum(c.other_fee) / 10000, 2) AS otherFee FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND c.site_code = \'${siteCode}\' AND [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id]) r\",\r\n	\"items\":[{\"alias\":\"teacherFee\",\"name\":\"师资费\"},{\"alias\":\"teachingFee\",\"name\":\"现场教学费\"},{\"alias\":\"transportFee\",\"name\":\"交通费\"},{\"alias\":\"foodFee\",\"name\":\"餐饮费\"},{\"alias\":\"roomFee\",\"name\":\"住宿费\"},{\"alias\":\"enrollFee\",\"name\":\"报名费\"},{\"alias\":\"materialFee\",\"name\": \"教材费\"}, {\"alias\": \"siteFee\",\"name\": \"场地费\"}, {\"alias\": \"periodFee\",\"name\": \"劳务带班费\"}, {\"alias\":\"otherFee\",\"name\": \"其他费用\"}, {\"alias\": \"balance\", \"name\": \"结余\"}],\r\n	\"columnPanel\": {\"sql\":\"SELECT round( sum(c.training_total_fee) / 10000, 2 ) AS incoming, round( sum( c.teacher_fee + c.teaching_fee + c.transport_fee + c.food_fee + c.room_fee + c.enroll_fee + c.material_fee + c.site_fee + c.period_fee + c.other_fee ) / 10000, 2 ) AS pay, round(( sum(c.training_total_fee) - sum( c.teacher_fee + c.teaching_fee + c.transport_fee + c.food_fee + c.room_fee + c.enroll_fee + c.material_fee + c.site_fee + c.period_fee + c.other_fee )) / 10000, 2 ) AS balanch FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND c.site_code = \'${siteCode}\' AND [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id]\",\r\n	\"columns\":[{\"alias\":\"incoming\",\"label\":\"总收入\",\"serial\":1,\"highlight\": false},{\"alias\": \"pay\",\"label\": \"总支出\",\"serial\":2,\"highlight\": false},{\"alias\": \"balanch\",\"label\": \"结余\",\"serial\": 3,\"highlight\": false}]},\r\n\"innerPieItems\": [{\"name\": \"结余\", \"mappingOutItems\": [\"balance\"]}, {\"name\": \"支出\", \"mappingOutItems\": [\"teacherFee\", \"teachingFee\", \"transportFee\", \"foodFee\", \"roomFee\", \"enrollFee\", \"materialFee\", \"siteFee\", \"periodFee\", \"otherFee\"]}]\r\n}', '4afc6da2173d11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('6abfa5b80f5311eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"isMapMetaData\": true,\r\n	\"sql\":\"SELECT round(r.total - r.teacherFee + r.teachingFee + r.transportFee + r.foodFee + r.roomFee + r.enrollFee + r.materialFee + r.siteFee + r.periodFee + r.otherFee, 2) AS balance, r.teacherFee AS teacherFee, r.teachingFee AS teachingFee, r.transportFee AS transportFee, r.foodFee AS foodFee, r.roomFee AS roomFee, r.enrollFee AS enrollFee, r.materialFee AS materialFee, r.siteFee AS siteFee, r.periodFee AS periodFee, r.otherFee AS otherFee FROM ( SELECT round( sum(c.training_total_fee) / 10000, 2 ) AS total, round(sum(c.teacher_fee) / 10000, 2) AS teacherFee, round( sum(c.teaching_fee) / 10000, 2 ) AS teachingFee, round( sum(c.transport_fee) / 10000, 2 ) AS transportFee, round(sum(c.food_fee) / 10000, 2) AS foodFee, round(sum(c.room_fee) / 10000, 2) AS roomFee, round(sum(c.enroll_fee) / 10000, 2) AS enrollFee, round( sum(c.material_fee) / 10000, 2 ) AS materialFee, round(sum(c.site_fee) / 10000, 2) AS siteFee, round(sum(c.period_fee) / 10000, 2) AS periodFee, round(sum(c.other_fee) / 10000, 2) AS otherFee FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' ) r\",\r\n	\"items\":[{\"alias\":\"teacherFee\",\"name\":\"师资费\"},{\"alias\":\"teachingFee\",\"name\":\"现场教学费\"},{\"alias\":\"transportFee\",\"name\":\"交通费\"},{\"alias\":\"foodFee\",\"name\":\"餐饮费\"},{\"alias\":\"roomFee\",\"name\":\"住宿费\"},{\"alias\":\"enrollFee\",\"name\":\"报名费\"},{\"alias\":\"materialFee\",\"name\": \"教材费\"}, {\"alias\": \"siteFee\",\"name\": \"场地费\"}, {\"alias\": \"periodFee\",\"name\": \"劳务带班费\"}, {\"alias\":\"otherFee\",\"name\": \"其他费用\"}, {\"alias\": \"balance\", \"name\": \"结余\"}],\r\n	\"columnPanel\": {\"sql\":\"SELECT round(sum(c.training_total_fee)/10000,2) AS incoming,round(sum(c.teacher_fee+c.teaching_fee+c.transport_fee+c.food_fee+c.room_fee+c.enroll_fee+c.material_fee+c.site_fee+c.period_fee+c.other_fee)/10000,2) AS pay,round((sum(c.training_total_fee)-sum(c.teacher_fee+c.teaching_fee+c.transport_fee+c.food_fee+c.room_fee+c.enroll_fee+c.material_fee+c.site_fee+c.period_fee+c.other_fee))/10000,2) AS balanch FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id=c.id INNER JOIN enum_const ty ON ty.id=cf.flag_flow_type INNER JOIN enum_const ss ON ss.id=c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id=cf.flag_active INNER JOIN enum_const cs ON cs.id=cf.flag_check_status WHERE ty.CODE=\'4\' AND ss.CODE=\'2\' AND ac.CODE=\'1\' AND cs.CODE=\'2\'\",\r\n	\"columns\":[{\"alias\":\"incoming\",\"label\":\"总收入\",\"serial\":1,\"highlight\": false},{\"alias\": \"pay\",\"label\": \"总支出\",\"serial\":2,\"highlight\": false},{\"alias\": \"balanch\",\"label\": \"结余\",\"serial\": 3,\"highlight\": false}]},\r\n\"innerPieItems\": [{\"name\": \"结余\", \"mappingOutItems\": [\"balance\"]}, {\"name\": \"支出\", \"mappingOutItems\": [\"teacherFee\", \"teachingFee\", \"transportFee\", \"foodFee\", \"roomFee\", \"enrollFee\", \"materialFee\", \"siteFee\", \"periodFee\", \"otherFee\"]}]\r\n}', '431ee10b0f5311eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('6d08859f1a6d11eabd12fcaa140ebf84', '{\"sql\": [\"SELECT concat( round( count(an.id) / count(stu.id) * 100, 1 ), \'%\' ) AS avgRate, concat( count(an.id) / count(DISTINCT stu.id), \'次\' ) AS avg, cast( concat( count(an.id), \'/\', count(stu.id), \'次\' ) AS CHAR ) AS complete FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN questionnaire_group hg ON hg.item_id = c.id INNER JOIN questionnaire_member mem ON mem.fk_group_id = hg.id AND mem.fk_sso_user_id = stu.fk_sso_user_id INNER JOIN enum_const ty ON ty.id = hg.flag_questionnaire_group_type LEFT JOIN questionnaire_answer an ON an.fk_group_id = hg.id AND an.fk_member_id = mem.id WHERE c.site_code = \'${siteCode}\' AND ty. CODE = \'1\' AND [parentId|c.id] AND hg.end_time < now() AND hg.is_delete = \'0\' AND [peUnit|c.fk_unit_id] AND [unit|c.fk_unit_id] AND [analyse_dateLimit|c.create_date]\"], \"items\": [{\"alias\": \"avgRate\", \"label\": \"平均回收率\", \"serial\": 1}, {\"alias\": \"avg\", \"label\": \"平均问卷次数\", \"serial\": 2}, {\"alias\": \"complete\", \"label\": \"回收人次\", \"serial\": 3}]}', '6d08859f1a6d11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('711155351b1b11eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"sql\":\"SELECT un. NAME AS `name`, round(( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee)) / 10000, 2 ) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id ORDER BY sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee) DESC LIMIT 0, 10\",\r\n	\"innerContentSql\": \"SELECT cast(concat(round(( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee)) / 10000, 2 ), \'万元\') as char) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id]\"\r\n}', '711155351b1b11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('736b9c56173811eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT r.xAxis as xAxis, r.incoming AS incoming, r.expense AS expense, r.incoming - r.expense AS balance FROM ( SELECT date_format( c.create_date, \'${dateFormat}\' ) AS xAxis, ifnull( round( sum(c.training_total_fee) / 10000, 2 ), 0 ) AS incoming, round( sum( ifnull(c.teacher_fee, 0) + ifnull(c.teaching_fee, 0) + ifnull(c.transport_fee, 0) + ifnull(c.food_fee, 0) + ifnull(c.room_fee, 0) + ifnull(c.enroll_fee, 0) + ifnull(c.material_fee, 0) + ifnull(c.site_fee, 0) + ifnull(c.period_fee, 0) + ifnull(c.other_fee, 0)) / 10000, 2 ) AS expense FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] GROUP BY date_format( c.create_date, \'${dateFormat}\' )) r\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"axis\": [{name: \"支出/收入\", \"key\": \"a1\", \"serial\": 1, \"unit\": \"万元\"}, {name: \"结余\", \"key\": \"a2\", \"serial\": 2, \"unit\": \"万元\"}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"expense\",\r\n			\"name\": \"支出\",\r\n			\"type\": \"bar\"\r\n		}, {\r\n			\"alias\": \"incoming\",\r\n			\"name\": \"收入\",\r\n			\"type\": \"bar\"\r\n		}, {\r\n			\"alias\": \"balance\",\r\n			\"name\": \"结余\",\r\n			\"type\": \"line\",\r\n			\"valueAxisKey\": \"a2\"\r\n		}\r\n	]}\r\n}', '4f6ccf3c173811eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('800d6a96173611eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(c.id) AS total, sum(IF(c.end_time < now(), 1, 0)) AS isEnd, sum(IF(c.start_time > now(), 1, 0)) AS noStart, sum( IF ( c.end_time >= now() AND c.start_time <= now(), 1, 0 )) AS inProgress FROM pe_class c WHERE [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"班级\", \"columns\": [{\"alias\": \"total\", \"label\": \"班级总数\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 2, \"highlight\": true}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 4, \"highlight\": false}]}', '6bae8417173611eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('80b538c51a5c11eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT date_Format( sul.LOGIN_DATETIME, \'${dateFormat}\' ) AS xAxis, count(DISTINCT stu.id) AS `value` FROM sso_user_login sul INNER JOIN pe_student stu ON stu.fk_sso_user_id = sul.FK_SSO_USER_ID INNER JOIN enum_const ty ON ty.id = sul.FLAG_LOGIN_TYPE WHERE ty. CODE = \'2\' AND [parentId|stu.fk_class_id] AND [items|date_Format( sul.LOGIN_DATETIME, \'${dateFormat}\' )] GROUP BY date_Format( sul.LOGIN_DATETIME, \'${dateFormat}\' )\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.BusinessGenerator\",\r\n		\"staticMethodName\": \"classActiveDayByParentId\"\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"name\": \"登录情况\",\r\n			\"type\": \"line\"\r\n		}\r\n	]}\r\n}', '80b538c51a5c11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('828bcc631a5811eabd12fcaa140ebf84', '{\"isCircle\": true, \"sql\": \"SELECT round( count( IF ( date_format( DATE_ADD( cct.training_date, INTERVAL 1 DAY ), \'%Y%m%d\' ) < date_format(now(), \'%Y%m%d\'), 1, 0 )) / count(cc.id) * 100, 1 ) AS percent FROM class_course cc LEFT JOIN class_course_timetable cct ON cct.fk_class_course_id = cc.id WHERE [parentId|cc.fk_class_id]\", items: [{\"alias\": \"percent\", serial: 1}]}', '828bcc631a5811eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('89184bed0f5f11eabd12fcaa140ebf84', '{\r\n	\"sql\":\"SELECT round(( count(inf.id) - sum(IF(st. CODE = \'1\', 1, 0))) / count(inf.id) * 100, 2 ) AS followedUp, round( sum(IF(st. CODE = \'2\', 1, 0)) / count(inf.id) * 100, 2 ) AS followingUp, round( sum(IF(st. CODE = \'3\', 1, 0)) / count(inf.id) * 100, 2 ) AS signed, round( sum(IF(st. CODE = \'1\', 1, 0)) / count(inf.id) * 100, 2 ) AS willFollowUp FROM requirement_info inf INNER JOIN enum_const st ON st.id = inf.flag_follow_up_status\",\r\n	\"items\":[{\"alias\":\"followedUp\",\"name\":\"已跟进\"},{\"alias\":\"followingUp\",\"name\":\"跟进中\"},{\"alias\":\"signed\",\"name\":\"已签约\"},{\"alias\":\"willFollowUp\",\"name\":\"待跟进\"}]\r\n}', '6c8d47220f5f11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('8ca61c941a5511eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"isMapMetaData\": true,\r\n	\"sql\":\"SELECT round(sum(c.teacher_fee) / 10000, 2) AS teacherFee, round( sum(c.teaching_fee) / 10000, 2 ) AS teachingFee, round( sum(c.transport_fee) / 10000, 2 ) AS transportFee, round(sum(c.food_fee) / 10000, 2) AS foodFee, round(sum(c.room_fee) / 10000, 2) AS roomFee, round(sum(c.enroll_fee) / 10000, 2) AS enrollFee, round( sum(c.material_fee) / 10000, 2 ) AS materialFee, round(sum(c.site_fee) / 10000, 2) AS siteFee, round(sum(c.period_fee) / 10000, 2) AS periodFee, round(sum(c.other_fee) / 10000, 2) AS otherFee FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [peUnit|c.fk_unit_id] AND [parentId|c.id]\",\r\n	\"items\":[{\"alias\":\"teacherFee\",\"name\":\"师资费\"},{\"alias\":\"teachingFee\",\"name\":\"现场教学费\"},{\"alias\":\"transportFee\",\"name\":\"交通费\"},{\"alias\":\"foodFee\",\"name\":\"餐饮费\"},{\"alias\":\"roomFee\",\"name\":\"住宿费\"},{\"alias\":\"enrollFee\",\"name\":\"报名费\"},{\"alias\":\"materialFee\",\"name\": \"教材费\"}, {\"alias\": \"siteFee\",\"name\": \"场地费\"}, {\"alias\": \"periodFee\",\"name\": \"劳务带班费\"}, {\"alias\":\"otherFee\",\"name\": \"其他费用\"}],\r\n	\"innerContentSql\": \"SELECT round(( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee)) / 10000, 2 ) AS total FROM pe_class c INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [peUnit|c.fk_unit_id] AND [parentId|c.id]\"\r\n}', '8ca61c941a5511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('96f454f2103511eabd12fcaa140ebf84', '{\"sql\": \"SELECT pr. NAME AS `name`, count(stu.id) AS `value` FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN training_item ti ON ti.id = c.fk_training_item_id INNER JOIN entrusted_unit eu ON eu.id = ti.fk_entrusted_unit_id INNER JOIN pe_area ar ON ar.id = eu.fk_area_id INNER JOIN enum_const pr ON ar.province LIKE concat(pr. NAME, \'%\') AND pr.namespace = \'flagprovince\' WHERE stu.site_code = \'${siteCode}\' GROUP BY pr.id\"}', '854f6cf9103511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('99ab36ce173011eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(ri.id) AS total, sum(IF(st. CODE = \'1\', 1, 0)) AS noFollow, sum(IF(st. CODE = \'2\', 1, 0)) AS following, sum(IF(st. CODE = \'3\', 1, 0)) AS sign, sum(IF(st. CODE = \'4\', 1, 0)) AS failure, sum(IF(st. CODE = \'5\', 1, 0)) AS delay FROM requirement_info ri INNER JOIN enum_const st ON ri.flag_follow_up_status = st.id INNER JOIN requirement_follow_up_info rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN pe_manager m ON m.id = rfui.fk_follow_up_user_id AND [peUnit|m.fk_unit_id] WHERE [unit|m.fk_unit_id] AND [analyse_dateLimit|ri.create_time] AND ri.site_code = \'${siteCode}\'\"], \"columns\": [{\"alias\": \"total\", \"label\": \"需求总数\", \"serial\": 1}, {\"alias\": \"noFollow\", \"label\": \"待跟进\", \"serial\": 2}, {\"alias\": \"following\", \"label\": \"跟进中\", \"serial\": 3, \"highlight\": true}, {\"alias\": \"sign\", \"label\": \"已签约\", \"serial\": 4}, {\"alias\": \"failure\", \"label\": \"失败\", \"serial\": 5}, {\"alias\": \"delay\", \"label\": \"延期\", \"serial\": 6}]}', '5dec8afc173011eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('9db07df917d911eabd12fcaa140ebf84', '{\r\n	\"type\": \"项目质量评估\",\r\n	\"indicators\": [\r\n		{\r\n			\"alias\": \"group\",\r\n			\"name\": \"组织管理\"\r\n		}, {\r\n			\"alias\": \"classMaster\",\r\n			\"name\": \"班主任评价\"\r\n		}, {\r\n			\"alias\": \"rearService\",\r\n			\"name\": \"后勤服务\"\r\n		}, {\r\n			\"alias\": \"course\",\r\n			\"name\": \"授课效果\"\r\n		}\r\n	],\r\n	\"series\": [\r\n		{\r\n			\"name\": \"质量评估\",\r\n			\"sql\": [\"SELECT round( sum(`group`) / count(`group`), 1 ) AS `group`, round( sum(classMaster) / count(classMaster), 1 ) AS classMaster, round( sum(rearService) / count(rearService), 1 ) AS rearService FROM ( SELECT round( sum( IF (ot. CODE = \'1\', ao. CODE + 1, 0)), 1 ) AS `group`, round( sum( IF (ot. CODE = \'2\', ao. CODE + 1, 0)), 1 ) AS `classMaster`, round( sum( IF (ot. CODE = \'3\', ao. CODE + 1, 0)), 1 ) AS `rearService` FROM overall_evaluate_option oeo INNER JOIN overall_evaluate_answer oea ON oeo.id = oea.fk_option_id INNER JOIN enum_const ot ON ot.id = oeo.flag_option_type INNER JOIN enum_const ao ON ao.id = oea.answer_id INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id INNER JOIN pe_class c ON c.id = stu.fk_class_id WHERE [analyse_dateLimit|c.create_date] AND [id|c.fk_unit_id] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] AND c.site_code = \'${siteCode}\' GROUP BY stu.id ) r\",\r\n\"SELECT round( sum(course) / count(course), 1 ) as course FROM ( SELECT sum(ao. CODE + 1) / count(c.id) AS `course` FROM overall_evaluate_answer oea INNER JOIN pe_course c ON c.id = oea.rel_id INNER JOIN enum_const ao ON ao.id = oea.answer_id INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id INNER JOIN pe_class cl ON cl.id = stu.fk_class_id WHERE [analyse_dateLimit|c.create_date] AND [id|cl.fk_unit_id] AND [unit|cl.fk_unit_id] AND [peUnit|cl.fk_unit_id] AND c.site_code = \'${siteCode}\' GROUP BY stu.id ) r\"]\r\n		}\r\n	]\r\n}', '6bce270317d911eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('9eac7fca1b2011eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT un. NAME AS `name`, round(( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee)) / 10000, 2 ) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id\"\r\n}', '9eac7fca1b2011eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('9f3fc9cc1bc911eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT unit. NAME AS xAxis,COUNT(cl.id) AS classNum FROM pe_manager pm INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND cl.fk_class_master_id = pm.id WHERE ([analyse_dateLimit|cl.start_time] OR [analyse_dateLimit|cl.end_time]) AND pm.site_code = \'${siteCode}\' AND [unit|cl.fk_unit_id] and [peUnit|cl.fk_unit_id] GROUP BY unit.id \",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n    \"xItemSql\": \"SELECT unit. NAME AS xAxis FROM pe_manager pm INNER JOIN pe_unit unit ON unit.id = pm.fk_unit_id INNER JOIN pe_class cl ON unit.id = cl.fk_unit_id AND cl.fk_class_master_id = pm.id WHERE ([analyse_dateLimit|cl.start_time] OR [analyse_dateLimit|cl.end_time]) AND pm.site_code = \'${siteCode}\' AND [unit|cl.fk_unit_id] and [peUnit|cl.fk_unit_id] GROUP BY unit.id \"\r\n	},\r\n	\"isReverse\": true,\r\n	\"axis\": [{name: \"数量\", \"key\": \"a1\", \"serial\": 1}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"classNum\",\r\n			\"name\": \"班级数量\",\r\n			\"type\": \"bar\",\r\n			\"valueAxisKey\": \"a1\"\r\n		}\r\n	]}\r\n}', '37a896a31bc911eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('a2e769221af411eabd12fcaa140ebf84', '{\"gridId\": \"classActivityGrid\", \"analyseCode\": \"classActivityGridDetailPie\"}', 'a2e769221af411eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('a3a554201a6911eabd12fcaa140ebf84', '{\"sql\": [\"SELECT concat( round( count(an.id) / count(stu.id) * 100, 1 ), \'%\' ) AS avgRate, concat( count(an.id) / count(DISTINCT stu.id), \'次\' ) AS avg, cast( concat( count(an.id), \'/\', count(stu.id), \'次\' ) AS CHAR ) AS complete FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN homework_group hg ON hg.item_id = c.id INNER JOIN homework_member mem ON mem.fk_group_id = hg.id AND mem.fk_sso_user_id = stu.fk_sso_user_id LEFT JOIN homework_answer an ON an.fk_group_id = hg.id AND an.fk_member_id = mem.id WHERE c.site_code = \'${siteCode}\' AND [parentId|c.id] AND hg.end_time < now() AND hg.is_delete = \'0\' AND [peUnit|c.fk_unit_id] AND [unit|c.fk_unit_id] AND [analyse_dateLimit|c.create_date]\"], \"items\": [{\"alias\": \"avgRate\", \"label\": \"平均完成率\", \"serial\": 1}, {\"alias\": \"avg\", \"label\": \"平均作业次数\", \"serial\": 2}, {\"alias\": \"complete\", \"label\": \"完成人次\", \"serial\": 3}]}', 'a3a554201a6911eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('a66ebc6617c411eabd12fcaa140ebf84', '{\r\n	\"showLegend\": true,\r\n	\"showLabel\": true,\r\n	\"isMapMetaData\": true,\r\n	\"sql\":\"SELECT sum(IF(ss. CODE = \'1\', 1, 0)) AS wait, sum(IF(ss. CODE = \'2\', 1, 0)) AS pass, sum(IF(ss. CODE = \'3\', 1, 0)) AS noPass FROM check_flow cf INNER JOIN enum_const ss ON ss.id = cf.flag_check_status INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type WHERE [analyse_dateLimit|cf.create_date] AND cf.site_code = \'${siteCode}\' AND [unit|cf.scope_id] AND [peUnit|cf.scope_id] AND [checkType|ty.id]\",\r\n	\"items\":[{\"alias\":\"wait\",\"name\":\"待审批\"}, {\"alias\":\"pass\",\"name\":\"审批通过\"}, {\"alias\":\"noPass\",\"name\":\"驳回审批\"}]\r\n}', '9f3f00c317c411eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('a6fe47a8174911eabd12fcaa140ebf84', '{\r\n	\"sql\": \"select date_format(c.create_Date, \'${dateFormat}\') as xAxis, count(DISTINCT stu.id) as student, count(DISTINCT c.id) as class from pe_student stu inner join pe_class c on c.id = stu.fk_class_id where [analyse_dateLimit|c.create_date] and c.site_code = \'${siteCode}\' and [unit|c.fk_unit_id] and [peUnit|c.fk_unit_id] group by date_format(c.create_date, \'${dateFormat}\')\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"axis\": [{name: \"班级\", \"key\": \"a1\", \"serial\": 1}, {name: \"学员\", \"key\": \"a2\", \"serial\": 2}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"class\",\r\n			\"name\": \"班级情况\",\r\n			\"type\": \"bar\",\r\n			\"valueAxisKey\": \"a1\"\r\n		}, {\r\n			\"alias\": \"student\",\r\n			\"name\": \"学员情况\",\r\n			\"type\": \"line\",\r\n			\"valueAxisKey\": \"a2\"\r\n		}\r\n	]}\r\n}', '8c106747174911eabd12fcaa140ebf84');
