INSERT INTO `analyse_config_detail` VALUES ('aa2d98d01b3611eabd12fcaa140ebf84', '{\r\n	\"extEChartConfig\": {\"xAxis\": {\"name\": \"跟进次数(次）\"}, \"yAxis\": {\"name\": \"成功率(%)\"}},\r\n	\"sql\": [\"SELECT round( sum(rfui.followNum) / count(ri.id), 1 ) AS `x`, round( sum(IF(st. CODE = \'3\', 1, 0)) / sum(IF(st. CODE IN(\'3\', \'4\'), 1, 0)) * 100, 2 ) AS `y`, un. NAME AS `label` FROM requirement_info ri INNER JOIN ( SELECT ri.id AS fk_requirement_info_id, count(rfui.id) AS followNum FROM requirement_follow_up_info rfui INNER JOIN requirement_info ri ON ri.id = rfui.fk_requirement_info_id WHERE [analyse_dateLimit|ri.create_time] AND ri.site_code = \'${siteCode}\' GROUP BY ri.id ) rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [analyse_dateLimit|ri.create_time] AND [peUnit|un.id] AND [unit|un.id] AND un.site_code = \'${siteCode}\' GROUP BY un.id\"]\r\n}', 'aa2d98d01b3611eabd12fcaa140ebf84');
