INSERT INTO `analyse_config_detail` VALUES ('b36bc9a71bc211eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT r.date AS xAxis, round(sum(score) / count(score), 1) AS avgScore FROM ( SELECT date_format( c.create_date, \'${dateFormat}\' ) AS date, sum(ao. CODE + 1) AS score FROM overall_evaluate_answer oea INNER JOIN enum_const ao ON ao.id = oea.answer_id INNER JOIN pe_Student stu ON stu.fk_sso_user_id = oea.fk_sso_user_id INNER JOIN pe_class c ON c.id = stu.fk_class_id WHERE c.site_code = \'${siteCode}\' AND [peUnit|un.id] AND [unit|un.id] AND [analyse_dateLimit|c.create_date] AND [items|date_format(c.create_date, \'${dateFormat}\')] GROUP BY stu.id ) r GROUP BY r.date\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"avgScore\",\r\n			\"name\": \"评分\",\r\n			\"type\": \"line\",\r\n			\"hasAverageLine\": true\r\n		}\r\n	]}\r\n}', 'b36bc9a71bc211eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('bfb471bd1b2011eabd12fcaa140ebf84', '{\r\n	\"unit\": \"万元\",\r\n	\"showLegend\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT un. NAME AS `name`, round(sum(training_total_fee) / 10000, 2 ) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id\"\r\n}', 'bfb471bd1b2011eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('c12bb82b173711eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(stu.id) AS total, sum(IF(c.end_time < now(), 1, 0)) AS isEnd, sum(IF(c.start_time > now(), 1, 0)) AS noStart, sum( IF ( c.end_time >= now() AND c.start_time <= now(), 1, 0 )) AS inProgress FROM pe_class c INNER JOIN pe_student stu ON stu.fk_class_id = c.id WHERE [analyse_dateLimit|c.create_date] AND c.site_code = \'${siteCode}\' AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id]\"],\r\n	\"title\": \"学员\", \"columns\": [{\"alias\": \"total\", \"label\": \"学员总数\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 2, \"highlight\": true}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 3, \"highlight\": false}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 4, \"highlight\": false}]}', 'b451c178173711eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('c28add8017c511eabd12fcaa140ebf84', '{\"sql\": \"SELECT pr. NAME AS `name`, count(stu.id) AS `value` FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN training_item ti ON ti.id = c.fk_training_item_id INNER JOIN entrusted_unit eu ON eu.id = ti.fk_entrusted_unit_id INNER JOIN pe_area ar ON ar.id = eu.fk_area_id INNER JOIN enum_const pr ON ar.province LIKE concat(pr. NAME, \'%\') AND pr.namespace = \'flagprovince\' WHERE stu.site_code = \'${siteCode}\' AND [analyse_dateLimit|c.create_date] AND [unit|c.fk_unit_id] AND [peUnit|c.fk_unit_id] GROUP BY pr.id\"}', 'a174311c17c511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('c702d8a31b1411eabd12fcaa140ebf84', '{\"gridId\": \"classQualityDetailGridRearService\", \"analyseCode\": \"classQualityDetailPie\"}', 'c702d8a31b1411eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('ce80504d1a2011eabd12fcaa140ebf84', '{\r\n	\"showLegend\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"select un.name as `name`, count(c.id) as `value` from pe_class c inner join pe_unit un on un.id = c.fk_unit_id where c.site_code = \'${siteCode}\' and [analyse_dateLimit|c.create_date] and [unit|c.fk_unit_id] and [peUnit|c.fk_unit_id] group by c.fk_unit_id order by count(c.id) desc\"\r\n}', 'ce80504d1a2011eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('cf656b6c128711eabd12fcaa140ebf84', '{\r\n	\"isReverse\": true,\r\n	\"sql\": \"SELECT date_Format( ti.training_start_time, \'%y-%m\' ) AS xAxis, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND ti.site_code = \'${siteCode}\' AND [items|date_Format(ti.training_start_time, \'%y-%m\')] GROUP BY date_Format( ti.training_start_time, \'%Y%m\' )\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"className\": \"com.whaty.analyse.framework.type.barline.xaxis.DateGenerator\",\r\n		\"staticMethodName\": \"monthOfLimit\"\r\n	},\r\n	\"axis\": [{name: \"项目数\", \"key\": \"a1\", \"serial\": 1, \"unit\": \"个\"}, {name: \"项目数2\", \"key\": \"a2\", \"serial\": 2}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"isEnd\",\r\n			\"name\": \"已结束\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"stack\",\r\n			\"hasAverageLine\": true,\r\n			\"valueAxisKey\": \"a1\"\r\n		}, {\r\n			\"alias\": \"inProgress\",\r\n			\"name\": \"进行中\",\r\n			\"type\": \"bar\",\r\n			\"group\": \"stack\"\r\n		}, {\r\n			\"alias\": \"noStart\",\r\n			\"name\": \"未开始\",\r\n			\"type\": \"bar\",\r\n      \"group\": \"stack\",\r\n			\"valueAxisKey\": \"a2\"\r\n		}\r\n	]}\r\n}', '9e6ae5b0128711eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('d5d2c2ee1b1611eabd12fcaa140ebf84', '{\r\n	\"showLegend\": false,\r\n	\"isMapMetaData\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT COUNT(IF(ao. CODE = \'0\', 1, NULL)) AS best, COUNT(IF(ao. CODE = \'1\', 1, NULL)) AS good, COUNT(IF(ao. CODE = \'2\', 1, NULL)) AS general, COUNT(IF(ao. CODE = \'3\', 1, NULL)) AS bad, count(stu.id) - count(oea.id) AS `none` FROM pe_class cl INNER JOIN pe_student stu ON stu.fk_class_id = cl.id INNER JOIN class_course cc ON cc.fk_class_id = cl.id LEFT JOIN overall_evaluate_answer oea ON oea.fk_sso_user_id = stu.fk_sso_user_id AND oea.rel_id = cc.fk_course_id LEFT JOIN enum_const ao ON ao.id = oea.answer WHERE [parentId|cl.id] AND [id|cc.fk_course_id]\",\r\n  \"items\": [{\"name\": \"优秀\", \"alias\": \"best\"}, {\"name\": \"良好\", \"alias\": \"good\"}, {\"name\": \"一般\", \"alias\": \"general\"}, {\"name\": \"很差\", \"alias\": \"bad\"}, {\"name\": \"弃评\", \"alias\": \"none\"}]\r\n}', 'd5d2c2ee1b1611eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('db3bab6b1b2311eabd12fcaa140ebf84', '{\r\n	\"sql\": \"SELECT un. NAME AS `xAxis`, ifnull( round(( sum(c.training_total_fee) - ( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee))) / sum(c.training_total_fee) * 100, 2 ), 0 ) AS `value` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id ORDER BY ( sum(c.training_total_fee) - ( sum(c.teacher_fee) + sum(c.teaching_fee) + sum(c.transport_fee) + sum(c.food_fee) + sum(c.room_fee) + sum(c.enroll_fee) + sum(c.material_fee) + sum(c.site_fee) + sum(c.period_fee) + sum(c.other_fee))) / sum(c.training_total_fee) DESC\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"xItemSql\": \"SELECT un. NAME AS `xAxis` FROM pe_class c INNER JOIN pe_unit un ON un.id = c.fk_unit_id INNER JOIN check_Flow cf ON cf.fk_item_id = c.id INNER JOIN enum_const ty ON ty.id = cf.flag_flow_type INNER JOIN enum_const ss ON ss.id = c.flag_apply_certificate_status INNER JOIN enum_const ac ON ac.id = cf.flag_active INNER JOIN enum_const cs ON cs.id = cf.flag_check_status WHERE ty. CODE = \'4\' AND ss. CODE = \'2\' AND ac. CODE = \'1\' AND cs. CODE = \'2\' AND [analyse_dateLimit|cf.complete_date] AND [unit|un.id] AND c.site_code = \'${siteCode}\' AND [peUnit|c.fk_unit_id] GROUP BY un.id\"\r\n	},\r\n  \"axis\": [{\"name\": \"结余率\", \"unit\": \"%\"}],\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"name\": \"结余率\",\r\n			\"type\": \"bar\"\r\n		}\r\n	]}\r\n}', 'db3bab6b1b2311eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('dc5a0b791bbc11eabd12fcaa140ebf84', '{\r\n	\"showLegend\": false,\r\n	\"isMapMetaData\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT sum(IF(st. CODE = \'1\', 1, 0)) AS noFollow, sum(IF(st. CODE = \'2\', 1, 0)) AS following, sum(IF(st. CODE = \'3\', 1, 0)) AS signed, sum(IF(st. CODE = \'4\', 1, 0)) AS failure, sum(IF(st. CODE = \'5\', 1, 0)) AS delay FROM requirement_info ri INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status WHERE [id|un.id]\",\r\n  \"items\": [{\"name\": \"待跟进\", \"alias\": \"noFollow\"}, {\"name\": \"跟进中\", \"alias\": \"following\"}, {\"name\": \"已签约\", \"alias\": \"signed\"}, {\"name\": \"失败\", \"alias\": \"failure\"}, {\"name\": \"延期\", \"alias\": \"delay\"}]\r\n}', 'dc5a0b791bbc11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('e13947851a5811eabd12fcaa140ebf84', '{\r\n	\"isReverse\": true,\r\n	\"sql\": \"SELECT ty. NAME AS xAxis, count(cc.id) AS `value` FROM class_course cc INNER JOIN pe_course c ON c.id = cc.fk_course_id INNER JOIN enum_const ty ON ty.id = c.flag_course_type WHERE [parentId|cc.fk_class_id] AND c.site_code = \'${siteCode}\' GROUP BY ty.id;\",\r\n	\"item\": {\r\n		\"alias\": \"xAxis\",\r\n		\"xItemSql\": \"select name from enum_const where namespace = \'flagCourseType\' and TEAM like \'%,${siteCode},%\'\"\r\n	},\r\n	\"series\": {\"items\": [\r\n		{\r\n			\"alias\": \"value\",\r\n			\"name\": \"课程数\",\r\n			\"type\": \"bar\"\r\n		}\r\n	]}\r\n}', 'e13947851a5811eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('e23fa80d1bc511eabd12fcaa140ebf84', '{\"gridId\": \"itemQualityUnitDetail\", \"analyseCode\": \"qualityRadar\"}', 'e23fa80d1bc511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('e2a7529d1a5311eabd12fcaa140ebf84', '{\"sql\": [\"select count(af.id) as attachNum from pe_class c inner join attach_file af on af.link_id = c.id where [parentId|c.id]\",\r\n\"select count(stu.id) as studentNum from pe_student stu inner join pe_class c on c.id = stu.fk_class_id where [parentId|c.id]\",\r\n\"select count(cc.id) as courseNum from class_course cc inner join pe_class c on c.id = cc.fk_class_id where [parentId|c.id]\"],\r\n	\"title\": \"班级信息\", \"columns\": [{\"alias\": \"studentNum\", \"label\": \"学员数量\", \"serial\": 1, \"highlight\": false}, {\"alias\": \"courseNum\", \"label\": \"课程数\", \"serial\": 2, \"highlight\": false}, {\"alias\": \"attachNum\", \"label\": \"附件数\", \"serial\": 3, \"highlight\": false}]}', 'e2a7529d1a5311eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('ea42260c180611eabd12fcaa140ebf84', '{\"gridId\": \"classAnalyse_classDetailAnalyse\", \"detailBoardId\": \"ad9488591a5311eabd12fcaa140ebf84\"}', 'e4328f6c180611eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('ed345821101511eabd12fcaa140ebf84', '{\"isCircle\": false, \"sql\": \"SELECT round( sum( IF ( ty. CODE = \'1\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'1\', 1, 0)) * 100, 2 ) AS `before`, round( sum( IF ( ty. CODE = \'2\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'2\', 1, 0)) * 100, 2 ) AS `inProgress`, round( sum( IF ( ty. CODE = \'3\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'3\', 1, 0)) * 100, 2 ) AS `after` FROM pe_class c INNER JOIN pe_manager m ON m.id = c.fk_class_master_id INNER JOIN prepare_item pi ON pi.site_code = \'training\' INNER JOIN enum_const ty ON ty.id = pi.flag_prepare_type LEFT JOIN teacher_prepare_item tpi ON c.id = tpi.fk_class_id AND tpi.fk_prepare_item_id = pi.id\", items: [{\"label\": \"训前工作\", \"alias\": \"before\", serial: 1}, {\"label\": \"训中工作\", \"alias\": \"inProgress\", serial: 2}, {\"label\": \"训后工作\", \"alias\": \"after\", serial: 3}, {\"label\": \"训后工作\", \"alias\": \"after\", serial: 4}]}', 'dd50d434101511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('efedd8c30ff111eabd12fcaa140ebf84', '{\"sql\": [\"SELECT count(ti.id) AS total, sum( IF ( ti.training_end_time < now(), 1, 0 )) AS isEnd, sum( IF ( ti.training_start_time > now(), 1, 0 )) AS noStart, sum( IF ( ti.training_end_time >= now() AND ti.training_start_time <= now(), 1, 0 )) AS inProgress FROM training_item ti INNER JOIN enum_const ty ON ty.id = ti.flag_training_item_type INNER JOIN pe_unit un ON un.id = ti.fk_unit_id AND [peUnit|un.id] INNER JOIN enum_const st ON st.id = ti.flag_item_status WHERE st. CODE = \'3\' AND [un|un.id] AND [date_dateLimit|ti.training_start_time] AND ti.site_code = \'${siteCode}\'\"],\r\n	\"title\": \"培训项目\", \"items\": [{\"alias\": \"total\", \"label\": \"项目总数\", \"serial\": 1}, {\"alias\": \"inProgress\", \"label\": \"进行中\", \"serial\": 4}, {\"alias\": \"isEnd\", \"label\": \"已结束\", \"serial\": 5}, {\"alias\": \"noStart\", \"label\": \"未开始\", \"serial\": 6}]}', 'd343e00c0ff111eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('f37de4cc1b1511eabd12fcaa140ebf84', '{\r\n	\"showLegend\": false,\r\n	\"isMapMetaData\": true,\r\n	\"showLabel\": true,\r\n	\"sql\":\"SELECT COUNT(IF(ao. CODE = \'0\', 1, NULL)) AS best, COUNT(IF(ao. CODE = \'1\', 1, NULL)) AS good, COUNT(IF(ao. CODE = \'2\', 1, NULL)) AS general, COUNT(IF(ao. CODE = \'3\', 1, NULL)) AS bad, count(stu.id) - count(oea.id) AS `none` FROM pe_class cl INNER JOIN pe_student stu ON stu.fk_class_id = cl.id INNER JOIN overall_evaluate_option oeo ON [id|oeo.id] LEFT JOIN overall_evaluate_answer oea ON oea.fk_sso_user_id = stu.fk_sso_user_id AND oea.fk_option_id = oeo.id LEFT JOIN enum_const ao ON ao.id = oea.answer WHERE [parentId|cl.id]\",\r\n  \"items\": [{\"name\": \"优秀\", \"alias\": \"best\"}, {\"name\": \"良好\", \"alias\": \"good\"}, {\"name\": \"一般\", \"alias\": \"general\"}, {\"name\": \"很差\", \"alias\": \"bad\"}, {\"name\": \"弃评\", \"alias\": \"none\"}]\r\n}', 'f37de4cc1b1511eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('f6e89fb7101f11eabd12fcaa140ebf84', '{\r\n	\"extEChartConfig\": {\"xAxis\": {\"name\": \"跟进次数(次）\"}, \"yAxis\": {\"name\": \"成功率(%)\"}},\r\n	\"sql\": [\"SELECT count(ri.id) AS `x`, round( sum(IF(st. CODE = \'3\', 1, 0)) / sum( IF ( st. CODE <> \'1\' AND st. CODE <> \'2\', 1, 9 )) * 100, 2 ) AS `y`, un.name as `label` FROM requirement_info ri INNER JOIN requirement_follow_up_info rfui ON rfui.fk_requirement_info_id = ri.id INNER JOIN pe_manager m ON m.id = ri.fk_create_user_id INNER JOIN pe_unit un ON un.id = m.fk_unit_id INNER JOIN enum_const st ON st.id = ri.flag_follow_up_status GROUP BY un.id\"]\r\n}', 'e88fd0e8101f11eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('fa31f3da157311eabd12fcaa140ebf84', '{\"isCircle\": true, \"sql\": \"SELECT 80 as percent\", items: [{\"alias\": \"percent\", serial: 1}]}', 'dbcd8255157311eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('faddd4841a6611eabd12fcaa140ebf84', '{\"sql\": [\"SELECT concat( round( count(css.id) / count(stu.id) * 100, 1 ), \'%\' ) AS avgSignRate, concat( count(css.id) / count(DISTINCT stu.id), \'次\' ) AS avgSign, cast(concat(count(css.id), \'/\', count(stu.id), \'次\' ) as char) AS completeSign FROM pe_student stu INNER JOIN pe_class c ON c.id = stu.fk_class_id INNER JOIN class_sign cs ON cs.fk_class_id = c.id LEFT JOIN class_sign_student css ON css.fk_class_sign_id = cs.id AND css.fk_student_id = stu.id WHERE c.site_code = \'${siteCode}\' AND [parentId|c.id] AND cs.end_time < now() AND [peUnit|c.fk_unit_id] AND [unit|c.fk_unit_id] AND [analyse_dateLimit|c.create_date]\"], \"items\": [{\"alias\": \"avgSignRate\", \"label\": \"平均签到率\", \"serial\": 1}, {\"alias\": \"avgSign\", \"label\": \"平均签到次数\", \"serial\": 2}, {\"alias\": \"completeSign\", \"label\": \"签到人次\", \"serial\": 3}]}', 'faddd4841a6611eabd12fcaa140ebf84');
INSERT INTO `analyse_config_detail` VALUES ('fba4009c1a5a11eabd12fcaa140ebf84', '{\"isCircle\": false, \"sql\": \"SELECT round( sum( IF ( ty. CODE = \'1\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'1\', 1, 0)) * 100, 2 ) AS `before`, round( sum( IF ( ty. CODE = \'2\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'2\', 1, 0)) * 100, 2 ) AS `inProgress`, round( sum( IF ( ty. CODE = \'3\' AND tpi.id IS NOT NULL, 1, 0 )) / sum(IF(ty. CODE = \'3\', 1, 0)) * 100, 2 ) AS `after` FROM pe_class c INNER JOIN pe_manager m ON m.id = c.fk_class_master_id INNER JOIN prepare_item pi ON pi.site_code = \'${siteCode}\' INNER JOIN enum_const ty ON ty.id = pi.flag_prepare_type LEFT JOIN teacher_prepare_item tpi ON c.id = tpi.fk_class_id AND tpi.fk_prepare_item_id = pi.id WHERE [parentId|c.id]\", items: [{\"label\": \"训前工作\", \"alias\": \"before\", serial: 1}, {\"label\": \"训中工作\", \"alias\": \"inProgress\", serial: 2}, {\"label\": \"训后工作\", \"alias\": \"after\", serial: 3}]}', 'fba4009c1a5a11eabd12fcaa140ebf84');
