
SET FOREIGN_KEY_CHECKS=0;

-- ----------------------------
-- Table structure for enroll_column
-- ----------------------------
DROP TABLE IF EXISTS `enroll_column`;
CREATE TABLE `enroll_column` (
  `id` varchar(32) NOT NULL,
  `data_index` varchar(50) NOT NULL COMMENT '索引',
  `flag_enroll_column_group` varchar(50) NOT NULL COMMENT '报名项分组',
  `name` varchar(50) NOT NULL,
  `flag_enroll_column_type` varchar(50) NOT NULL COMMENT '项类型',
  `reg_exp` varchar(50) DEFAULT NULL COMMENT '正则表达式',
  `exp_error_info` varchar(50) DEFAULT NULL COMMENT '校验错误提示',
  `custom_sql` varchar(255) DEFAULT NULL COMMENT '自定义sql',
  `note` varchar(255) DEFAULT NULL COMMENT '备注',
  `example_picture` varchar(255) DEFAULT NULL COMMENT '示例图片',
  `flag_is_attach_file` varchar(50) DEFAULT NULL COMMENT '如果是文件，是否是附件，是的话会使用student_[data_index]创建一个namespace，且可以有多个文件，否则在field中查找',
  `file_num` int(3) DEFAULT NULL COMMENT '可上传的文件数量',
  PRIMARY KEY (`id`),
  UNIQUE KEY `enroll_column_unique` (`data_index`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for enroll_column_regulation
-- ----------------------------
DROP TABLE IF EXISTS `enroll_column_regulation`;
CREATE TABLE `enroll_column_regulation` (
  `id` int(9) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL COMMENT '名称',
  `flag_active` varchar(50) NOT NULL DEFAULT '1' COMMENT '是否有效',
  `flag_is_default` varchar(50) NOT NULL DEFAULT '0' COMMENT '是否默认',
  `note` varchar(255) DEFAULT NULL COMMENT '备注',
  `site_code` varchar(50) NOT NULL COMMENT '所属站点',
  `create_by` varchar(50) NOT NULL,
  `create_date` datetime NOT NULL,
  `update_by` varchar(50) DEFAULT NULL,
  `update_date` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `enroll_item_regulation_unique` (`site_code`,`name`) USING BTREE,
  KEY `enroll_item_regulation_default_unique` (`flag_is_default`,`site_code`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for enroll_column_regulation_detail
-- ----------------------------
DROP TABLE IF EXISTS `enroll_column_regulation_detail`;
CREATE TABLE `enroll_column_regulation_detail` (
  `id` int(9) NOT NULL AUTO_INCREMENT,
  `fk_regulation_id` int(9) NOT NULL,
  `fk_column_id` varchar(32) NOT NULL,
  `is_active` char(1) NOT NULL DEFAULT '1',
  `is_required` char(1) NOT NULL DEFAULT '1',
  `serial_number` int(3) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `enroll_column_regulation_detail_unique` (`fk_regulation_id`,`fk_column_id`) USING BTREE,
  KEY `enroll_column_regulation_column_fk` (`fk_column_id`),
  CONSTRAINT `enroll_column_regulation_detail_ibfk_1` FOREIGN KEY (`fk_column_id`) REFERENCES `enroll_column` (`id`),
  CONSTRAINT `enroll_column_regulation_detail_ibfk_2` FOREIGN KEY (`fk_regulation_id`) REFERENCES `enroll_column_regulation` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;


