,\n  cooperateUnit.telephone as cooperateUnitTelephone,\n  cooperateUnit.linkman_position as cooperateUnitLinkmanPosition,\n  cooperateUnit.division_proportion as cooperateUnitDivisionProportion,\n  entrustedUnit.name as entrustedUnitName,\n  enumConstByFlagEntrustedUnitType.name as entrustedUnitTypeName,\n  entrustedUnitArea.name as entrustedUnitAreaName,\n  enumConstByFlagInfoSource.name as infoSourceName,\n  enumConstByFlagInfoSource.id as infoSourceId,\n  entrustedUnit.id as entrustedUnitId,\n  concat(man.true_name,\'/\',man.login_id) as headName,\n  man.id as manId,\n  this.fk_enroll_regulation_id as regulation,\n  nc.id as needCheckId,\n  nc.name as needCheck\nFROM\n	training_item this \nINNER JOIN pe_unit peUnit on peUnit.id=this.fk_unit_id AND [peUnit|peUnit.id]\nINNER JOIN enum_const enumConstByFlagItemStatus on enumConstByFlagItemStatus.id=this.flag_item_status\nINNER JOIN enum_const enumConstByFlagTrainingType on enumConstByFlagTrainingType.id=this.flag_training_type\nINNER JOIN enum_const enumConstByFlagTrainingTarget on enumConstByFlagTrainingTarget.id=this.flag_training_target\nINNER JOIN enum_const enumConstByFlagIsLegal on enumConstByFlagIsLegal.id=this.flag_is_legal\nINNER JOIN enum_const enumConstByFlagTrainingItemType on enumConstByFlagTrainingItemType.id=this.flag_training_item_type\nAND enumConstByFlagTrainingItemType.code = \'1\'\nINNER JOIN enum_const et1 on et1.NAMESPACE=\'FlagItemExtendType\' and et1.code=\'1\'\nINNER JOIN enum_const et2 on et2.NAMESPACE=\'FlagItemExtendType\' and et2.code=\'2\'\nINNER JOIN enum_const et3 on et3.NAMESPACE=\'FlagItemExtendType\' and et3.code=\'3\'\nINNER JOIN enum_const et4 on et4.NAMESPACE=\'FlagItemExtendType\' and et4.code=\'4\'\nLEFT JOIN sso_user createBy on createBy.id=this.create_by\nLEFT JOIN pe_manager man on man.id=this.fk_manager_id\nLEFT JOIN sso_user checkUser on checkUser.id=this.fk_check_user_id\nLEFT JOIN training_item_extend trainingItemExtend on trainingItemExtend.fk_item_id=this.id\nAND trainingItemExtend.flag_item_extend_type=et1.id\nLEFT JOIN training_item_extend trainingItemExtend2 on trainingItemExtend2.fk_item_id=this.id\nAND trainingItemExtend2.flag_item_extend_type=et2.id\nLEFT JOIN training_item_extend trainingItemExtend3 on trainingItemExtend3.fk_item_id=this.id\nAND trainingItemExtend3.flag_item_extend_type=et3.id\nLEFT JOIN training_item_extend trainingItemExtend4 on trainingItemExtend4.fk_item_id=this.id\nAND trainingItemExtend4.flag_item_extend_type=et4.id\nLEFT JOIN enum_const enumConstByFlagInfoSource on enumConstByFlagInfoSource.id=this.flag_info_source\nLEFT JOIN cooperate_unit cooperateUnit on cooperateUnit.id=this.fk_cooperate_unit_id\nLEFT JOIN enum_const enumConstByFlagCooperateUnitType on enumConstByFlagCooperateUnitType.id=cooperateUnit.flag_cooperate_unit_type\nLEFT JOIN pe_area cooperateUnitArea on cooperateUnitArea.id=cooperateUnit.fk_area_id\nLEFT JOIN entrusted_unit entrustedUnit on entrustedUnit.id=this.fk_entrusted_unit_id\nLEFT JOIN enum_const enumConstByFlagEntrustedUnitType on enumConstByFlagEntrustedUnitType.id=entrustedUnit.flag_entrusted_unit_type\nLEFT JOIN pe_area entrustedUnitArea on entrustedUnitArea.id=entrustedUnit.fk_area_id\nLEFT JOIN enum_const nc on nc.id = this.flag_enroll_need_check\nwhere this.site_code = \'${siteCode}\'' WHERE (`id`='societyTrainingItemApply');
