UPDATE `grid_basic_config` SET `sqlstr`='SELECT\n  stu.id as id,\n	stu.true_name AS name,\n	stu.card_no AS cardNo,\n	stu.mobile AS mobile,\n        stu.email as email,\n	stu.work_unit AS workUnit,\n	stu.enroll_fee AS enrollFee,\n	IF(stu.positional_title is NULL,stu.professional_title,stu.positional_title) AS professionalTitle,\n	unit.NAME AS trainUnitName,\n	item.id AS itemId,\n	item.CODE AS itemCode,\n	item.NAME AS itemName,\n	cl.CODE AS classCode,\n	cl.NAME AS className,\n	stu.create_date AS createDate,\n	enumConstByFlagFeeStatus .NAME AS feeSatus,\n	enumConstByFlagPayWay .NAME AS payWay,\n        po.order_number as orderNumber,\n	po.pay_date AS payDate,\n	st.NAME AS studentStatus,\n	stu.quit_school_date AS quitSchoolDate,\n	stu.invoice_no AS invoiceNo,\n	stu.invoice_print_time AS invoicePrintTime,\n	tt.NAME AS ticketType,\n        (case tt.code \n		WHEN \'1\' THEN stu.true_name\n		WHEN \'2\' THEN stu.invoice_unit_name\n		ELSE \'\'\n		END\n	) AS invoiceTitle,\n	stu.invoice_address AS invoiceAddress,\n	stu.invoice_address_zip_code AS zipCode,\n  po.money_order_link as photo,\n  ifnull(ch.name, \'无需审核\') as checkStatus,\n   stu.enroll_check_note as checkNote\nFROM\n	pe_student stu\nleft JOIN training_item item ON item.id = stu.fk_training_item_id\nleft JOIN pe_unit unit ON unit.id = item.fk_unit_id AND [peUnit|unit.id]\nleft JOIN enum_const enumConstByFlagFeeStatus ON enumConstByFlagFeeStatus .id = stu.flag_fee_status\nleft JOIN enum_const st ON st.id = stu.flag_student_status\nleft JOIN enum_const tt ON tt.id = stu.flag_invoice_ticket_type\nINNER JOIN enum_const valid ON valid.namespace = \'flagIsvalid\'\nAND valid. CODE = \'1\'\nLEFT JOIN pe_order po ON po.fk_student_id = stu.id\nAND po.item_id = item.id\nAND po.flag_isvalid = valid.id\nLEFT JOIN enum_const enumConstByFlagPayWay ON enumConstByFlagPayWay .id = po.flag_pay_way\nLEFT JOIN pe_class cl ON cl.id = stu.fk_class_id\nLEFT JOIN enum_const ch on ch.id = stu.flag_enroll_check_status\nwhere stu.site_code = \'${siteCode}\'\nand (enumConstByFlagFeeStatus.id is not null or st.id is not null)\nand stu.fk_training_item_id is not null' WHERE (`id`='studentEnrollInfoManage');
INSERT INTO `grid_column_config` (`ID`, `fk_action_grid_config_id`, `name`, `dataindex`, `data_column`, `search`, `to_add`, `to_update`, `column_can_update`, `list`, `report`, `type`, `dateFormat`, `allow_blank`, `max_length`, `check_message`, `check_regular`, `text_field_parameters`, `combo_sql`, `serial_number`, `flag_active`, `flag_bak`, `user_type`, `team`, `s_note`, `sql_result`, `is_html`, `default_text`, `cascade_columns`) VALUES ('4028ae3170c754990170c75bd5720000', 'studentEnrollInfoManage', '审核意见', 'checkNote', '', '1', '0', '0', '0', '1', '1', 'TextField', 'yyyy-MM-dd', '1', '50', '', '', NULL, '', '25', '4028809c1d625bcf011d66fd0dda0006', NULL, NULL, '', '', '', NULL, '', NULL);
